        try {
            GSSManager manager = GSSManager.getInstance();
            Oid mechOid = new Oid(mechOID);
            GSSCredential server = manager.createCredential(null, GSSCredential.INDEFINITE_LIFETIME, new Oid[]{mechOid}, GSSCredential.SERVER_CREDENTIAL);

            GSSServer serverInstance = new GSSServer();
            serverInstance.s = new ServerSocket(port);
