                Socket client = serverInstance.s.accept();
                serverInstance.processClient(server, client);
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    private void processClient(GSSCredential server, Socket client) {
        try {
            GSSContext aCtxt = null;
            DataInputStream dis = new DataInputStream(client.getInputStream());
            DataOutputStream dos = new DataOutputStream(client.getOutputStream());

            try {
                aCtxt = GSSManager.getInstance().createContext(dis, server, null, 0);
            } catch (GSSException e) {
                print("Error creating context: " + e.getMessage());
                client.close();
                return;
            }

            byte[] token = new byte[0];
            boolean established = false;

            while (!established) {
                try {
                    token = aCtxt.acceptSecContext(token, 0, token.length);
                } catch (GSSException e) {
                    print("Error accepting security context: " + e.getMessage());
                    client.close();
                    return;
                }

                if (token != null && token.length > 0) {
                    dos.write(token);
                    dos.flush();
                }

                established = aCtxt.isEstablished();
            }

            exchangeWithPeer(aCtxt, dis, dos);
            aCtxt.dispose();
            client.close();
        } catch (IOException | GSSException e) {
            print("Error processing client: " + e.getMessage());
        }
    }

    private void exchangeWithPeer(GSSContext aCtxt, DataInputStream dis, DataOutputStream dos) throws IOException, GSSException {
        byte[] buffer = new byte[1024];
        int bytesRead;
        byte[] token;

        print("Secure context established. Starting message exchange.");

