            token = aCtxt.unwrap(buffer, 0, bytesRead);
            String msg = new String(token);
            print("Received: " + msg);

            String response = "Echo: " + msg;
            token = aCtxt.wrap(response.getBytes(), 0, response.length());
            dos.write(token);
            dos.flush();
        }
    }

    private void usage() {
        print("Usage: java GSSServer [-port <portNumber>] [-mech <mechanismOID>] <serviceName>");
    }

    private void print(String msg) {
        System.out.println(msg);
    }

