import java.io.InputStream;
import java.io.IOException;
import java.net.HttpURLConnection;
import java.net.URL;
import java.util.Map;

public class HttpFetcher {
    private static final int MAX_REDIRECTS = 10;

    public static InputStream fetch(String urlStr) {
        if (!urlStr.startsWith("http://") && !urlStr.startsWith("https://")) {
            return null;
        }

        HttpURLConnection connection = null;
        InputStream inputStream = null;
        int redirectCount = 0;

        try {
            while (redirectCount <= MAX_REDIRECTS) {
                URL url = new URL(urlStr);
                connection = (HttpURLConnection) url.openConnection();
                connection.setInstanceFollowRedirects(false);
                connection.setRequestMethod("GET");
                connection.setConnectTimeout(5000);
                connection.setReadTimeout(10000);

                Map<String, String> cookies = getCookieHeader(connection);
                if (cookies != null) {
                    for (Map.Entry<String, String> entry : cookies.entrySet()) {
                        connection.addRequestProperty("Cookie", entry.getKey() + "=" + entry.getValue());
                    }
                }

                int responseCode = connection.getResponseCode();
                if (responseCode >= 300 && responseCode < 400) {
                    String location = connection.getHeaderField("Location");
                    if (location == null) {
                        break;
                    }
                    urlStr = location;
                    redirectCount++;
                    if (redirectCount > MAX_REDIRECTS) {
                        break;
                    }
                } else {
                    inputStream = connection.getInputStream();
                    break;
                }
            }
        } catch (IOException e) {
            e.printStackTrace();
        } finally {
            if (connection != null) {
                connection.disconnect();
            }
        }

        return inputStream;
    }

    private static Map<String, String> getCookieHeader(HttpURLConnection connection) {
        String cookieHeader = connection.getHeaderField("Set-Cookie");
        if (cookieHeader == null) {
            return null;
        }

        Map<String, String> cookies = new java.util.HashMap<>();
        String[] cookiePairs = cookieHeader.split("; ");
        for (String pair : cookiePairs) {
            String[] keyValue = pair.split("=");
            if (keyValue.length == 2) {
                cookies.put(keyValue[0], keyValue[1]);
            }
        }
        return cookies;
    }
}

