public class WebSocketServer {
    private ServerSocket serverSocket;
    private int port;
    private ExecutorService executor = Executors.newCachedThreadPool();

    public WebSocketServer(int port) {
        this.port = port;
    }

    public void start() {
        try {
            serverSocket = new ServerSocket(port);
            System.out.println("WebSocket server started on port " + port);
            while (true) {
                Socket clientSocket = serverSocket.accept();
                executor.execute(() -> handleConnection(clientSocket));
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private void handleConnection(Socket clientSocket) {
        try (BufferedReader in = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));
             BufferedWriter out = new BufferedWriter(new OutputStreamWriter(clientSocket.getOutputStream()))) {

            String handshakeLine = in.readLine();
            if (handshakeLine == null || !handshakeLine.startsWith("GET")) {
                return;
            }

            String upgradeHeader = null;
            String connectionHeader = null;
            String secWebSocketKey = null;

            String line;
            while ((line = in.readLine()) != null && !line.isEmpty()) {
                if (line.startsWith("Upgrade: ")) {
                    upgradeHeader = line.substring(7).trim();
                } else if (line.startsWith("Connection: ")) {
                    connectionHeader = line.substring(11).trim();
                } else if (line.startsWith("Sec-WebSocket-Key: ")) {
                    secWebSocketKey = line.substring(19).trim();
                }
            }

            if (upgradeHeader == null || !upgradeHeader.equals("websocket") ||
                connectionHeader == null || !connectionHeader.contains("Upgrade")) {
                return;
            }

            String acceptKey = Base64.getEncoder().encodeToString(
                SHA1.digest((secWebSocketKey + "258EAFA5-E914-47DA-95CA-C5AB0DC85B11").getBytes())
            );

            String response = "HTTP/1.1 101 Switching Protocols\r\n" +
                              "Upgrade: websocket\r\n" +
                              "Connection: Upgrade\r\n" +
                              "Sec-WebSocket-Accept: " + acceptKey + "\r\n\r\n";

            out.write(response);
            out.flush();

            sendBinaryFrame(out, "Hello from WebSocket server!".getBytes());

            // Handle incoming frames
            byte[] buffer = new byte[1024];
            int bytesRead;
            while ((bytesRead = in.read(buffer)) != -1) {
                byte[] frame = new byte[bytesRead];
                System.arraycopy(buffer, 0, frame, 0, bytesRead);
                // Process frame here if needed
            }

        } catch (IOException e) {
            e.printStackTrace();
        } finally {
            try {
                clientSocket.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private void sendBinaryFrame(BufferedWriter out, byte[] data) {
        try {
            boolean masked = true;
            int mask = (int) (Math.random() * Integer.MAX_VALUE);
            byte[] maskBytes = new byte[4];
            for (int i = 0; i < 4; i++) {
                maskBytes[i] = (byte) (mask >> (i * 8));
            }

            byte[] payload = new byte[data.length];
            for (int i = 0; i < data.length; i++) {
                payload[i] = (byte) (data[i] ^ maskBytes[i % 4]);
            }

            ByteBuffer frame = ByteBuffer.allocate(2 + data.length + (masked ? 4 : 0));
            frame.put((byte) 0x82); // Binary frame with no FIN bit set
            frame.put((byte) (masked ? (0x80 | data.length) : data.length));
            if (masked) {
                frame.put(maskBytes);
            }
            frame.put(payload);

            out.write(frame.array());
            out.flush();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void stop() {
        try {
            if (serverSocket != null && !serverSocket.isClosed()) {
                serverSocket.close();
            }
            executor.shutdownNow();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private static byte[] SHA1 digest(byte[] input) {
        try {
            MessageDigest md = MessageDigest.getInstance("SHA-1");
            return md.digest(input);
        } catch (NoSuchAlgorithmException e) {
            throw new RuntimeException(e);
        }
    }
}

