import com.netflix.appinfo.ApplicationInfoManager;
import com.netflix.appinfo.InstanceInfo;
import com.netflix.discovery.EurekaClient;
import com.netflix.discovery.shared.Application;

import java.io.*;
import java.net.ServerSocket;
import java.net.Socket;
import java.time.LocalDateTime;
import java.time.format.DateTimeFormatter;

public class ExampleServiceBase {
    private final ApplicationInfoManager applicationInfoManager;
    private final EurekaClient eurekaClient;
    private ServerSocket serverSocket;
    private boolean isRunning = false;

    public ExampleServiceBase(ApplicationInfoManager applicationInfoManager, EurekaClient eurekaClient) {
        this.applicationInfoManager = applicationInfoManager;
        this.eurekaClient = eurekaClient;
    }

    public void start() {
        try {
            applicationInfoManager.setInstanceStatus(InstanceInfo.InstanceStatus.UP);
            int port = getRegisteredPort();
            serverSocket = new ServerSocket(port);
            isRunning = true;
            System.out.println("Server started on port: " + port);
            new Thread(this::listenForConnections).start();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private int getRegisteredPort() {
        InstanceInfo instanceInfo = applicationInfoManager.getInfo();
        return instanceInfo.getPort();
    }

    private void listenForConnections() {
        while (isRunning) {
            try {
                Socket clientSocket = serverSocket.accept();
                new Thread(() -> processRequest(clientSocket)).start();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private void processRequest(Socket clientSocket) {
        try {
            BufferedReader in = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));
            PrintWriter out = new PrintWriter(clientSocket.getOutputStream(), true);

            String request = in.readLine();
            System.out.println("Received request: " + request);

            String response = "Response at: " + LocalDateTime.now().format(DateTimeFormatter.ISO_DATE_TIME);
            out.println(response);

            clientSocket.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void stop() {
        isRunning = false;
        try {
            if (serverSocket != null && !serverSocket.isClosed()) {
                serverSocket.close();
            }
            applicationInfoManager.setInstanceStatus(InstanceInfo.InstanceStatus.DOWN);
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}

