            + "Connection: Upgrade\r\n"
            + "Sec-WebSocket-Accept: s3pPLMBiTxaQ9kYGzzh1zQ==\r\n"
            + "\r\n";

    public WebSocketHandshakeServer(int port) {
        this.port = port;
    }

    public void start() {
        try {
            serverSocket = new ServerSocket(port);
            System.out.println("Server started on port " + port);
            ExecutorService executor = Executors.newCachedThreadPool();
            while (!serverSocket.isClosed()) {
                Socket client = serverSocket.accept();
                executor.execute(() -> handleClientConnection(client));
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void stop() {
        try {
            if (serverSocket != null && !serverSocket.isClosed()) {
                serverSocket.close();
                System.out.println("Server stopped.");
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private void handleClientConnection(Socket client) {
        try (BufferedReader in = new BufferedReader(new InputStreamReader(client.getInputStream()));
             BufferedWriter out = new BufferedWriter(new OutputStreamWriter(client.getOutputStream()))) {

            String requestLine = in.readLine();
            if (requestLine == null) return;

            String[] requestParts = requestLine.split(" ");
            String path = requestParts[1];

            String response = "";
            if (path.equals("/valid")) {
                response = "HTTP/1.1 101 Switching Protocols\r\n"
                        + "Upgrade: websocket\r\n"
                        + "Connection: Upgrade\r\n"
                        + "Sec-WebSocket-Accept: s3pPLMBiTxaQ9kYGzzh1zQ==\r\n"
                        + "\r\n";
            } else if (path.equals("/invalid")) {
                response = "HTTP/1.1 400 Bad Request\r\n"
                        + "Content-Type: text/plain\r\n"
                        + "Content-Length: 12\r\n"
                        + "\r\n"
                        + "Invalid request";
            } else if (path.equals("/missingupgrade")) {
                response = "HTTP/1.1 400 Bad Request\r\n"
                        + "Content-Type: text/plain\r\n"
                        + "Content-Length: 17\r\n"
                        + "\r\n"
                        + "Missing Upgrade header";
            } else {
                response = "HTTP/1.1 404 Not Found\r\n"
                        + "Content-Type: text/plain\r\n"
                        + "Content-Length: 9\r\n"
                        + "\r\n"
                        + "Not found";
            }

            out.write(response);
            out.flush();
        } catch (IOException e) {
            e.printStackTrace();
        } finally {
            try {
                client.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private byte[] asciiBytes(String input) {
        return input.getBytes();
    }

    public static void main(String[] args) {
        if (args.length < 1) {
            System.out.println("Usage: java WebSocketHandshakeServer <port>");
            return;
        }
        int port = Integer.parseInt(args[0]);
        WebSocketHandshakeServer server = new WebSocketHandshakeServer(port);
