    private boolean enableProxy = false;
    private boolean running = false;
    private Socket client;
    private Socket server;
    private ServerSocket serverSocket;

    public ConnectionManipulationProxyServer(String host, int remotePort, int localPort) {
        this.host = host;
        this.remotePort = remotePort;
        this.localPort = localPort;
    }

    public void startProxy() {
        synchronized (enableLock) {
            if (enableProxy) return;
            enableProxy = true;
            running = true;
            proxyThread = new Thread(this::run);
            proxyThread.start();
        }
    }

    public void stopProxy() {
        synchronized (enableLock) {
            enableProxy = false;
            running = false;
            killOpenSockets();
        }
    }

    public void killOpenSockets() {
        try {
            if (client != null) {
                client.close();
            }
            if (server != null) {
                server.close();
            }
            if (serverSocket != null) {
                serverSocket.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void run() {
        try {
            serverSocket = new ServerSocket(localPort);
            while (running) {
                client = serverSocket.accept();
                try {
                    server = new Socket(host, remotePort);
                    new Thread(() -> forwardTraffic(client, server)).start();
                    new Thread(() -> forwardTraffic(server, client)).start();
                } catch (IOException e) {
                    e.printStackTrace();
                    client.close();
                }
            }
        } catch (IOException e) {
            e.printStackTrace();
        } finally {
            killOpenSockets();
        }
    }

    private void forwardTraffic(Socket from, Socket to) {
        try {
            InputStream input = from.getInputStream();
            OutputStream output = to.getOutputStream();
            byte[] buffer = new byte[4096];
            int bytesRead;
            while ((bytesRead = input.read(buffer)) != -1 && enableProxy) {
                output.write(buffer, 0, bytesRead);
            }
        } catch (IOException e) {
            e.printStackTrace();
        } finally {
            try {
                if (from != null) from.close();
                if (to != null) to.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    public int getLocalPort() {
