import java.io.*;
import java.net.*;
import java.util.*;

public abstract class EmbedHttpServer implements Runnable {
    protected int port;
    protected ServerSocket serverSocket;
    protected boolean running = false;

    public EmbedHttpServer(int port) {
        this.port = port;
    }

    public void start() {
        try {
            serverSocket = new ServerSocket(port);
            running = true;
            new Thread(this).start();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void stop() {
        running = false;
        try {
            if (serverSocket != null && !serverSocket.isClosed()) {
                serverSocket.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void run() {
        while (running) {
            try {
                Socket clientSocket = serverSocket.accept();
                new Thread(() -> handleRequest(clientSocket)).start();
            } catch (IOException e) {
                if (running) {
                    e.printStackTrace();
                }
            }
        }
    }

    protected void handleRequest(Socket clientSocket) {
        try {
            BufferedReader input = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));
            BufferedWriter output = new BufferedWriter(new OutputStreamWriter(clientSocket.getOutputStream()));

            String requestLine = input.readLine();
            if (requestLine == null) {
                return;
            }

            String[] requestParts = requestLine.split(" ");
            String method = requestParts[0];
            String path = requestParts[1];

            HashMap<String, String> headers = new HashMap<>();
            String headerLine;
            while ((headerLine = input.readLine()) != null && !headerLine.isEmpty()) {
                int colonIndex = headerLine.indexOf(":");
                if (colonIndex > 0) {
                    String headerName = headerLine.substring(0, colonIndex).trim();
                    String headerValue = headerLine.substring(colonIndex + 1).trim();
                    headers.put(headerName, headerValue);
                }
            }

            Map<String, String> queries = parsePath(path);
            InputStream inputStream = clientSocket.getInputStream();
            OutputStream outputStream = clientSocket.getOutputStream();

            handle(method, path, headers, queries, inputStream, outputStream);

            output.flush();
            clientSocket.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public abstract void handle(String method, String path, HashMap<String, String> headers, Map<String, String> queries, InputStream input, OutputStream response);

    protected Map<String, String> parsePath(String path) {
        Map<String, String> result = new HashMap<>();
        if (path.contains("?")) {
            String[] parts = path.split("\\?");
            String[] queryParts = parts[1].split("&");
            for (String queryPart : queryParts) {
                String[] keyValue = queryPart.split("=");
                if (keyValue.length == 2) {
                    result.put(keyValue[0], keyValue[1]);
                }
            }
        }
        return result;
    }
}

