            out.write("HTTP/1.1 200 OK\r\n".getBytes());
            out.write("Content-Type: text/plain\r\n".getBytes());
            out.write("Connection: close\r\n\r\n".getBytes());
            out.write("Hello, World!\r\n".getBytes());
        } else {
            replyError(out, new IOException("Unsupported method: " + cmd));
        }
    }

    private void replyError(OutputStream out, Exception e) throws IOException {
        out.write(("HTTP/1.1 500 Internal Server Error\r\n" +
                "Content-Type: text/plain\r\n" +
                "Connection: close\r\n\r\n" +
                e.getMessage() + "\r\n").getBytes());
    }

    private class ServiceThread extends Thread {
        Webserver web;
        Socket sock;

        public ServiceThread(Webserver w, Socket s) {
            web = w;
            sock = s;
        }

        public void run() {
            web.process(sock);
        }
    }

    public static void main(String[] args) {
        try {
            Webserver server = new Webserver(8080);
            server.run();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}

