                serverSocket.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public abstract void handle(String method, String path, HashMap<String, String> headers, InputStream input, OutputStream response);

    public void run() {
        try {
            while (!serverSocket.isClosed()) {
                Socket clientSocket = serverSocket.accept();
                new Thread(() -> {
                    try {
                        BufferedReader in = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));
                        BufferedWriter out = new BufferedWriter(new OutputStreamWriter(clientSocket.getOutputStream()));

                        String requestLine = in.readLine();
                        if (requestLine == null) {
                            clientSocket.close();
                            return;
                        }

                        String[] requestParts = requestLine.split(" ");
                        String method = requestParts[0];
                        String path = requestParts[1];

                        HashMap<String, String> headers = new HashMap<>();
                        String headerLine;
                        while ((headerLine = in.readLine()) != null && !headerLine.isEmpty()) {
                            String[] headerParts = headerLine.split(": ", 2);
                            if (headerParts.length == 2) {
                                headers.put(headerParts[0], headerParts[1]);
                            }
                        }

                        int contentLength = 0;
                        if (headers.containsKey("Content-Length")) {
                            contentLength = Integer.parseInt(headers.get("Content-Length"));
                        }

                        InputStream input = new BodyInputStream(clientSocket.getInputStream(), contentLength);
                        OutputStream response = clientSocket.getOutputStream();

                        handle(method, path, headers, input, response);

                        clientSocket.close();
                    } catch (IOException e) {
                        e.printStackTrace();
                    }
                }).start();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    protected class BodyInputStream extends FilterInputStream {
        private int limit;
        private int bytesRead;

        public BodyInputStream(InputStream ins, int n) {
            super(ins);
            this.limit = n;
            this.bytesRead = 0;
        }

        public int available() {
            return Math.min(super.available(), limit - bytesRead);
        }

        public int read() throws IOException {
            if (bytesRead >= limit) {
                return -1;
            }
            int b = super.read();
            if (b != -1) {
                bytesRead++;
            }
            return b;
        }

        public int read(byte[] b, int off, int len) throws IOException {
            if (bytesRead >= limit) {
                return -1;
            }
            int read = super.read(b, off, Math.min(len, limit - bytesRead));
            if (read > 0) {
                bytesRead += read;
            }
            return read;
        }

        public long skip(long n) throws IOException {
            throw new IOException("Skipping not supported");
        }

        public void close() throws IOException {
            super.close();
        }
    }
}

