    private InputStream in;
    private OutputStream out;
    private SocketAddress dest;
    private String request;
    private String passcode;
    private Consumer<byte[]> on_rsp;

    public Object start(InetAddress bind_addr, long timeout, int ttl) {
        try {
            sock = new Socket();
            sock.bind(bind_addr);
            sock.connect(dest, (int) timeout);
            sock.setSoTimeout((int) timeout);
            sock.setTcpNoDelay(true);
            sock.setKeepAlive(true);
            sock.setSoLinger(true, 0);
            sock.setTrafficClass(ttl);

            in = sock.getInputStream();
            out = sock.getOutputStream();

            return CompletableFuture.completedFuture(true);
        } catch (Exception e) {
            return CompletableFuture.failedFuture(e);
        }
    }

    public Object stop() {
        try {
            if (sock != null && !sock.isClosed()) {
                sock.close();
            }
            return CompletableFuture.completedFuture(true);
        } catch (Exception e) {
            return CompletableFuture.failedFuture(e);
        }
    }

    public boolean isRunning() {
        return sock != null && !sock.isClosed();
    }

    public Object sendRequest(byte[] request) {
        try {
            if (out != null) {
                out.write(request);
                out.flush();
            }
            return CompletableFuture.completedFuture(true);
        } catch (Exception e) {
            return CompletableFuture.failedFuture(e);
        }
    }

    public byte[] fetchResponse() {
        try {
            ByteArrayOutputStream buffer = new ByteArrayOutputStream();
            byte[] data = new byte[1024];
            int bytesRead;
            while ((bytesRead = in.read(data)) != -1) {
                buffer.write(data, 0, bytesRead);
            }
            byte[] response = buffer.toByteArray();
            if (on_rsp != null) {
                on_rsp.accept(response);
            }
            return response;
        } catch (Exception e) {
            return new byte[0];
        }
    }

    public void setOnRsp(Consumer<byte[]> on_rsp) {
        this.on_rsp = on_rsp;
    }

    public void setDest(SocketAddress dest) {
        this.dest = dest;
    }

    public void setRequest(String request) {
        this.request = request;
    }

    public void setPasscode(String passcode) {
        this.passcode = passcode;
    }
