public class TcpClient {
    public static long sendToServer(URI uri, InputStream inputStream, Object parser) throws IOException, InterruptedException, URISyntaxException {
        Socket socket = new Socket();
        socket.connect(new InetSocketAddress(uri.getHost(), uri.getPort()), 5000);
        if (!socket.isConnected()) {
            throw new IOException("Failed to connect to server");
        }

        OutputStream outputStream = socket.getOutputStream();
        AtomicLong totalBytesSent = new AtomicLong(0);

        parser.start();

        byte[] buffer = new byte[4096];
        int bytesRead;
        while ((bytesRead = inputStream.read(buffer)) != -1) {
            outputStream.write(buffer, 0, bytesRead);
            totalBytesSent.addAndGet(bytesRead);
        }

        outputStream.flush();
        parser.stop();

        if (totalBytesSent.get() <= 0) {
            throw new IOException("No data was transmitted");
        }

        socket.close();
        return totalBytesSent.get();
    }
}

