    public CustomSocketFactory(String proxyHost, int proxyPort, String proxyUser, String proxyPass, Proxy.Type proxyType) {
        this.proxyHost = proxyHost;
        this.proxyPort = proxyPort;
        this.proxyUser = proxyUser;
        this.proxyPass = proxyPass;
        this.proxyType = proxyType;
    }

    public CustomSocketFactory() {
        // Default constructor for direct connections
    }

    @Override
    public Socket createSocket(String host, int port) throws IOException {
        return createSocket(host, port, null, -1);
    }

    @Override
    public Socket createSocket(String host, int port, InetAddress localHost, int localPort) throws IOException {
        Socket socket = new Socket();
        if (proxyType != null && proxyType != Proxy.Type.DIRECT) {
            Proxy proxy = new Proxy(proxyType, new InetSocketAddress(proxyHost, proxyPort));
            socket = new Socket(proxy);
        } else {
            socket.bind(new InetSocketAddress(localHost, localPort));
            socket.connect(new InetSocketAddress(host, port));
        }
        connectToProxy(socket);
        return socket;
    }

    @Override
    public Socket createSocket(InetAddress host, int port) throws IOException {
        return createSocket(host.getHostAddress(), port);
    }

    @Override
    public Socket createSocket(InetAddress address, int port, InetAddress localAddress, int localPort) throws IOException {
        return createSocket(address.getHostAddress(), port, localAddress, localPort);
    }

    @Override
    public Socket createSocket() throws IOException {
        Socket socket = new Socket();
        if (proxyType != null && proxyType != Proxy.Type.DIRECT) {
            Proxy proxy = new Proxy(proxyType, new InetSocketAddress(proxyHost, proxyPort));
            socket = new Socket(proxy);
        }
        return socket;
    }

    private void connectToProxy(Socket socket) throws IOException {
        if (proxyType == Proxy.Type.HTTP) {
            OutputStream out = socket.getOutputStream();
            String requestLine = "CONNECT " + socket.getInetAddress().getHostAddress() + ":" + socket.getPort() + " HTTP/1.1\r\n";
            out.write(requestLine.getBytes());
            if (proxyUser != null && proxyPass != null) {
                String authStr = getBasicAuthStr();
                out.write(("Proxy-Authorization: Basic " + authStr + "\r\n").getBytes());
            }
            out.write("Host: " + socket.getInetAddress().getHostAddress() + ":" + socket.getPort() + "\r\n");
            out.write("Connection: close\r\n\r\n");
            out.flush();

            InputStream in = socket.getInputStream();
            String statusLine = readLine(in);
            int responseCode = getResponseCode(statusLine);
            if (responseCode != 200) {
                throw new IOException("HTTP proxy connection failed with response code: " + responseCode);
            }
        }
    }

    private String getBasicAuthStr() {
        String auth = proxyUser + ":" + proxyPass;
        return Base64.getEncoder().encodeToString(auth.getBytes());
    }

    private String readLine(InputStream in) throws IOException {
        StringBuilder sb = new StringBuilder();
        int c;
        while ((c = in.read()) != -1) {
            if (c == '\n') {
                break;
            }
            sb.append((char) c);
        }
        return sb.toString();
    }

    private static int getResponseCode(String statusLine) {
        String[] parts = statusLine.split(" ");
        if (parts.length >= 2) {
            try {
                return Integer.parseInt(parts[1]);
            } catch (NumberFormatException e) {
                return -1;
            }
        }
        return -1;
    }
}

