        try {
            if (server) {
                sock = new DatagramSocket(port);
                log.info("UDP server started on port " + port);
            } else {
                sock = new DatagramSocket();
                log.info("UDP client initialized");
            }
            receiver_thread = new Thread(new Receiver());
            receiver_thread.start();
        } catch (IOException e) {
            log.error("Failed to start UDP transport: " + e.getMessage());
        }
    }

    public void stop() {
        if (sock != null) {
            sock.close();
            log.info("UDP transport stopped");
        }
        if (receiver_thread != null) {
            receiver_thread.interrupt();
        }
    }

    public void send(Object dest, byte[] data, int offset, int length) {
        if (dest instanceof SocketAddress) {
            SocketAddress address = (SocketAddress) dest;
            try {
                DatagramPacket packet = new DatagramPacket(data, offset, length, address);
                sock.send(packet);
                log.debug("Sent UDP packet to " + address);
