        @Override
        public void run() {
            byte[] buffer = new byte[65535];
            while (!Thread.currentThread().isInterrupted()) {
                DatagramPacket packet = new DatagramPacket(buffer, buffer.length);
                try {
                    sock.receive(packet);
                    log.debug("Received UDP packet from " + packet.getSocketAddress());
                    if (receiver != null) {
                        receiver.onReceive(packet.getData(), packet.getOffset(), packet.getLength(), packet.getSocketAddress());
