import java.net.DatagramPacket;
import java.net.DatagramSocket;
import java.net.InetAddress;
import java.net.SocketTimeoutException;
import java.util.concurrent.TimeUnit;

public class SntpImpl implements Sntp {
    public static final int NTP_PORT = 123;
    public static final int NTP_MODE = 3; // Client mode
    public static final int NTP_VERSION = 3;
    public static final int NTP_PACKET_SIZE = 48;
    public static final long OFFSET_1900_TO_1970 = 2208988800L;

    @Override
    public SntpResult requestTime(InetAddress serverAddress, float maxOffset, float maxStratum, int timeoutMillis, int retries, SntpEventListener listener) {
        byte[] buffer = new byte[NTP_PACKET_SIZE];
        writeNtpVersion(buffer);
        writeTimeStamp(buffer, 40, System.currentTimeMillis() / 1000 + OFFSET_1900_TO_1970);

        for (int attempt = 0; attempt < retries; attempt++) {
            try (DatagramSocket socket = new DatagramSocket()) {
                socket.setSoTimeout(timeoutMillis);
                DatagramPacket requestPacket = new DatagramPacket(buffer, buffer.length, serverAddress, NTP_PORT);
                socket.send(requestPacket);

                DatagramPacket responsePacket = new DatagramPacket(buffer, buffer.length);
                socket.receive(responsePacket);

                long receiveTimestamp = System.currentTimeMillis();
                long originateTimestamp = readTimeStamp(buffer, 40);
                long receiveTimestampNtp = readTimeStamp(buffer, 44);
                long transmitTimestamp = readTimeStamp(buffer, 48);

                long offset = ((transmitTimestamp - originateTimestamp) - (receiveTimestampNtp - receiveTimestamp)) / 2;
                long delay = (receiveTimestampNtp - originateTimestamp) - (transmitTimestamp - receiveTimestamp);

                if (Math.abs(offset) <= maxOffset * 1e6 && read(buffer, 0) <= maxStratum) {
                    long ntpTime = transmitTimestamp - OFFSET_1900_TO_1970;
                    double milliseconds = doubleMillis(ntpTime);
                    return new SntpResult(milliseconds, offset, delay, true);
                }
            } catch (SocketTimeoutException e) {
                if (listener != null) {
                    listener.onTimeout(attempt + 1, retries);
                }
            } catch (Exception e) {
                if (listener != null) {
                    listener.onError(e.getMessage());
                }
            }
            if (listener != null) {
                listener.onRetry(attempt + 1, retries);
            }
            try {
                TimeUnit.MILLISECONDS.sleep(1000);
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
        }
        return new SntpResult(0.0, 0, 0, false);
    }

    private void writeNtpVersion(byte[] buffer) {
        byte flags = (byte) ((NTP_VERSION << 3) | NTP_MODE);
        buffer[0] = flags;
    }

    private void writeTimeStamp(byte[] buffer, int offset, long seconds) {
        long fraction = (seconds & 0xFFFFFFFFL) << 32;
        buffer[offset] = (byte) (fraction >> 56);
        buffer[offset + 1] = (byte) (fraction >> 48);
        buffer[offset + 2] = (byte) (fraction >> 40);
        buffer[offset + 3] = (byte) (fraction >> 32);
        buffer[offset + 4] = (byte) (fraction >> 24);
        buffer[offset + 5] = (byte) (fraction >> 16);
        buffer[offset + 6] = (byte) (fraction >> 8);
        buffer[offset + 7] = (byte) (fraction);
    }

    private long readTimeStamp(byte[] buffer, int offset) {
        long seconds = read(buffer, offset);
        long fraction = read(buffer, offset + 4);
        return seconds + (fraction / 4294967296.0);
    }

    private long read(byte[] buffer, int offset) {
        return ((buffer[offset] & 0xFFL) << 24) |
               ((buffer[offset + 1] & 0xFFL) << 16) |
               ((buffer[offset + 2] & 0xFFL) << 8) |
               (buffer[offset + 3] & 0xFFL);
    }

    private int ui(byte b) {
        return b & 0xFF;
    }

    private double doubleMillis(long ntpTime) {
        return (ntpTime - OFFSET_1900_TO_1970) * 1000.0;
    }
}

