import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.LinkedBlockingQueue;
import java.util.concurrent.TimeUnit;

public class Statsd {
    private static final Charset CHARSET = Charset.forName("US-ASCII");
    private static final String DEFAULT_HOST = "localhost";
    private static final int DEFAULT_PORT = 8125;
    private static final int MAX_BATCH_SIZE = 1500; // Max UDP packet size
    private static final int SLEEP_MS = 100;

    private static Statsd instance;
    private final ExecutorService e = Executors.newSingleThreadExecutor();
    private final LinkedBlockingQueue<String> queue = new LinkedBlockingQueue<>();
    private final InetAddress hostAddress;
    private final int port;
    private DatagramSocket socket;

    private Statsd(String host, int port) throws Exception {
        this.hostAddress = InetAddress.getByName(host);
        this.port = port;
        socket = new DatagramSocket();
        startBackgroundThread();
    }

    public static Statsd getInstance() {
        if (instance == null) {
            try {
                instance = new Statsd(DEFAULT_HOST, DEFAULT_PORT);
            } catch (Exception ex) {
                throw new RuntimeException("Failed to initialize Statsd client", ex);
            }
        }
        return instance;
    }

    public void increment(String metricName) {
        queue.offer(metricName + ":1|c");
    }

    public void gauge(String metricName, double value) {
        queue.offer(metricName + ":" + value + "|g");
    }

    public void timing(String metricName, long value) {
        queue.offer(metricName + ":" + value + "|ms");
    }

    private void startBackgroundThread() {
