import java.net.DatagramPacket;
import java.net.DatagramSocket;
import java.net.InetAddress;
import java.net.SocketException;
import java.net.UnknownHostException;

public class UDPClient {
    private DatagramSocket mUdpSocket;
    private boolean mRunning;
    private Thread mThread;

    public void sendUDPPacket(String targetAddress, int port, String data, boolean waitResponse, ResponseHandler handler) {
        if (mUdpSocket == null) {
            try {
                mUdpSocket = new DatagramSocket();
            } catch (SocketException e) {
                handler.onError("Failed to create UDP socket: " + e.getMessage());
                return;
            }
        }

        try {
            byte[] sendData = data.getBytes();
            InetAddress address = InetAddress.getByName(targetAddress);
            DatagramPacket sendPacket = new DatagramPacket(sendData, sendData.length, address, port);
            mUdpSocket.send(sendPacket);

            if (waitResponse) {
                mRunning = true;
                mThread = new Thread(() -> {
                    try {
                        byte[] receiveData = new byte[1024];
                        DatagramPacket receivePacket = new DatagramPacket(receiveData, receiveData.length);
                        mUdpSocket.receive(receivePacket);
                        String response = new String(receivePacket.getData(), 0, receivePacket.getLength());
                        handler.onResponse(response);
                    } catch (IOException e) {
                        handler.onError("Error receiving response: " + e.getMessage());
                    } finally {
                        mRunning = false;
                    }
                });
                mThread.start();
            }
        } catch (UnknownHostException e) {
            handler.onError("Unknown host: " + e.getMessage());
        } catch (IOException e) {
            handler.onError("Error sending packet: " + e.getMessage());
        }
    }

    public void stop() {
        if (mUdpSocket != null) {
            mUdpSocket.close();
            mUdpSocket = null;
        }
        if (mThread != null && mRunning) {
            mThread.interrupt();
        }
        mRunning = false;
    }

    public interface ResponseHandler {
        void onResponse(String response);
        void onError(String errorMessage);
    }
}

