import java.net.DatagramPacket;
import java.net.DatagramSocket;
import java.net.InetAddress;
import java.net.SocketAddress;
import java.util.Random;
import java.util.concurrent.Executors;
import java.util.concurrent.ScheduledExecutorService;
import java.util.concurrent.TimeUnit;

public class SingleClientUdpTracker {
    private static final long CONNECTION_EXPIRATION_TIME = 60 * 1000;
    private static final long EPHEMERAL_CONNECTION_ID = 0x41727101980L;
    private static final int CONNECT_CODE = 0;
    private static final int ANNOUNCE_CODE = 1;
    private static final int ERROR_CODE = -1;
    private DatagramSocket serverSocket;
    private final int interval;
    private final int leechers;
    private final int seeders;
    private boolean shutdown;
    private long currentConnection;
    private long connectedOn;
    private final ScheduledExecutorService scheduler = Executors.newScheduledThreadPool(1);

    public SingleClientUdpTracker(int interval, int leechers, int seeders) {
        this.interval = interval;
        this.leechers = leechers;
        this.seeders = seeders;
        try {
            serverSocket = new DatagramSocket();
            serverSocket.setSoTimeout(1000);
            currentConnection = EPHEMERAL_CONNECTION_ID;
            connectedOn = System.currentTimeMillis();
            scheduler.scheduleAtFixedRate(this::checkExpiration, 10, 10, TimeUnit.SECONDS);
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    public SocketAddress getServerAddress() {
        return serverSocket.getLocalSocketAddress();
    }

    public void run() {
        byte[] buffer = new byte[512];
        while (!shutdown) {
            try {
                DatagramPacket packet = new DatagramPacket(buffer, buffer.length);
                serverSocket.receive(packet);
                byte[] requestData = packet.getData();
                byte[] response = processRequest(requestData);
                DatagramPacket responsePacket = new DatagramPacket(response, response.length, packet.getSocketAddress());
                serverSocket.send(responsePacket);
            } catch (Exception e) {
                e.printStackTrace();
            }
        }
    }

    private byte[] processRequest(byte[] requestData) {
        int action = bytesToInt(requestData, 0);
        int transactionId = bytesToInt(requestData, 4);
        if (action == CONNECT_CODE) {
            return createConnectResponse(currentConnection, transactionId);
        } else if (action == ANNOUNCE_CODE) {
            return createAnnounceResponse(currentConnection, transactionId);
        } else {
            return createError(transactionId, "Unsupported action");
        }
    }

    private long generateConnectionId() {
        Random random = new Random();
        return (long) (random.nextDouble() * Long.MAX_VALUE);
    }

    private byte[] createConnectResponse(long connectionId, int transactionId) {
        byte[] response = new byte[16];
        intToBytes(connectionId, response, 0);
        intToBytes(transactionId, response, 8);
        intToBytes(0, response, 12);
        return response;
    }

    private byte[] createAnnounceResponse(long connectionId, int transactionId) {
        byte[] response = new byte[20];
        intToBytes(connectionId, response, 0);
        intToBytes(transactionId, response, 8);
        intToBytes(interval, response, 12);
        intToBytes(leechers, response, 16);
        intToBytes(seeders, response, 20);
        return response;
    }

    private byte[] createError(int transactionId, String message) {
        byte[] response = new byte[1 + 4 + message.length()];
        response[0] = (byte) ERROR_CODE;
        intToBytes(transactionId, response, 1);
        byte[] messageBytes = message.getBytes();
        System.arraycopy(messageBytes, 0, response, 5, messageBytes.length);
        return response;
    }

    private void checkExpiration() {
        if (!shutdown && System.currentTimeMillis() - connectedOn > CONNECTION_EXPIRATION_TIME) {
            currentConnection = generateConnectionId();
            connectedOn = System.currentTimeMillis();
        }
    }

    public void shutdown() {
        shutdown = true;
        serverSocket.close();
        scheduler.shutdown();
    }

    private void intToBytes(int value, byte[] bytes, int offset) {
        bytes[offset] = (byte) (value >> 24);
        bytes[offset + 1] = (byte) (value >> 16);
        bytes[offset + 2] = (byte) (value >> 8);
        bytes[offset + 3] = (byte) value;
    }

    private int bytesToInt(byte[] bytes, int offset) {
        return ((bytes[offset] & 0xFF) << 24) |
               ((bytes[offset + 1] & 0xFF) << 16) |
               ((bytes[offset + 2] & 0xFF) << 8) |
               (bytes[offset + 3] & 0xFF);
    }
}

