public class DnsUdpClient {
    public static final InetSocketAddress[] DNS_SERVERS = {
        new InetSocketAddress("8.8.8.8", 53),
        new InetSocketAddress("8.8.4.4", 53)
    };

    public List<Inet4Address> resolveAOverUdp(InetSocketAddress dnsServer, byte[] query) throws Exception {
        DatagramSocket socket = new DatagramSocket();
        socket.setSoTimeout(5000);
        DatagramPacket request = new DatagramPacket(query, query.length, dnsServer);
        socket.send(request);

        byte[] buffer = new byte[65535];
        DatagramPacket response = new DatagramPacket(buffer, buffer.length);
        socket.receive(response);

        socket.close();
        return toIps(buffer);
    }

    private List<Inet4Address> toIps(byte[] buffer) {
        List<Inet4Address> ips = new ArrayList<>();
        int offset = 12;
        int flags = ByteBuffer.wrap(buffer, 2, 2).getShort() & 0xFFFF;
        if ((flags & 0x8000) != 0) return ips;

        int questionCount = ByteBuffer.wrap(buffer, 4, 2).getShort() & 0xFFFF;
        int answerCount = ByteBuffer.wrap(buffer, 6, 2).getShort() & 0xFFFF;

        for (int i = 0; i < answerCount; i++) {
            int nameOffset = offset;
            int length = buffer[offset++] & 0xFF;
            while (length != 0) {
                offset += length;
                length = buffer[offset++] & 0xFF;
            }

            short type = ByteBuffer.wrap(buffer, offset, 2).getShort() & 0xFFFF;
            offset += 2;
            short className = ByteBuffer.wrap(buffer, offset, 2).getShort() & 0xFFFF;
            offset += 2;
            int ttl = ByteBuffer.wrap(buffer, offset, 4).getInt();
            offset += 4;
            short dataLength = ByteBuffer.wrap(buffer, offset, 2).getShort() & 0xFFFF;
            offset += 2;

            if (type == 1 && className == 1) {
                for (int j = 0; j < dataLength; j += 4) {
                    byte[] ipBytes = Arrays.copyOfRange(buffer, offset + j, offset + j + 4);
                    try {
                        ips.add((Inet4Address) Inet4Address.getByAddress(ipBytes));
                    } catch (UnknownHostException e) {
                        // Handle invalid IP
                    }
                }
                offset += dataLength;
            }
        }
        return ips;
    }

    public String resolveTXTOverUdp(InetSocketAddress dnsServer, byte[] query) throws Exception {
        DatagramSocket socket = new DatagramSocket();
        socket.setSoTimeout(5000);
        DatagramPacket request = new DatagramPacket(query, query.length, dnsServer);
        socket.send(request);

        byte[] buffer = new byte[65535];
        DatagramPacket response = new DatagramPacket(buffer, buffer.length);
        socket.receive(response);

        socket.close();
        return toTXT(buffer);
    }

    private String toTXT(byte[] buffer) {
        int offset = 12;
        int flags = ByteBuffer.wrap(buffer, 2, 2).getShort() & 0xFFFF;
        if ((flags & 0x8000) != 0) throw new RuntimeException("DNS query failed");

        int questionCount = ByteBuffer.wrap(buffer, 4, 2).getShort() & 0xFFFF;
        int answerCount = ByteBuffer.wrap(buffer, 6, 2).getShort() & 0xFFFF;

        for (int i = 0; i < answerCount; i++) {
            int nameOffset = offset;
            int length = buffer[offset++] & 0xFF;
            while (length != 0) {
                offset += length;
                length = buffer[offset++] & 0xFF;
            }

            short type = ByteBuffer.wrap(buffer, offset, 2).getShort() & 0xFFFF;
            offset += 2;
            short className = ByteBuffer.wrap(buffer, offset, 2).getShort() & 0xFFFF;
            offset += 2;
            int ttl = ByteBuffer.wrap(buffer, offset, 4).getInt();
            offset += 4;
            short dataLength = ByteBuffer.wrap(buffer, offset, 2).getShort() & 0xFFFF;
            offset += 2;

            if (type == 16 && className == 1) {
                byte[] txtData = Arrays.copyOfRange(buffer, offset, offset + dataLength);
