import java.io.IOException;
import java.io.InputStream;
import java.io.InputStreamReader;
import java.net.HttpURLConnection;
import java.net.URL;
import org.json.JSONObject;
import org.json.JSONArray;

public class HttpRequester {
    public static HttpURLConnection getConnectionFromRoute(String apiUrl, Route route, String... params) throws IOException {
        String url = route.build(apiUrl, params);
        URL obj = new URL(url);
        HttpURLConnection connection = (HttpURLConnection) obj.openConnection();
        connection.setRequestMethod(route.getMethod().name());
        connection.setRequestProperty("User-Agent", "Mozilla/5.0");
        return connection;
    }

    public static HttpURLConnection getConnectionFromCompiledRoute(String apiUrl, Route.CompiledRoute route) throws IOException {
        String url = route.getUrl().replace("{apiUrl}", apiUrl);
        URL obj = new URL(url);
        HttpURLConnection connection = (HttpURLConnection) obj.openConnection();
        connection.setRequestMethod(route.getMethod().name());
        connection.setRequestProperty("User-Agent", "Mozilla/5.0");
        return connection;
    }

    public static String parseInputStreamAndClose(InputStream inputStream) throws IOException {
        BufferedReader reader = new BufferedReader(new InputStreamReader(inputStream));
        StringBuilder result = new StringBuilder();
        String line;
        while ((line = reader.readLine()) != null) {
            result.append(line);
        }
        reader.close();
        return result.toString();
    }

    public static String parseString(HttpURLConnection connection) throws IOException {
        InputStream inputStream = connection.getInputStream();
        return parseInputStreamAndClose(inputStream);
    }

    public static String parseStringAndDisconnect(HttpURLConnection connection) throws IOException {
        String response = parseString(connection);
        connection.disconnect();
        return response;
    }

    public static String parseErrorString(HttpURLConnection connection) throws IOException {
        InputStream errorStream = connection.getErrorStream();
        return parseInputStreamAndClose(errorStream);
    }

    public static String parseErrorStringAndDisconnect(HttpURLConnection connection) throws IOException {
        String errorResponse = parseErrorString(connection);
        connection.disconnect();
        return errorResponse;
    }

    public static JSONObject parseJSONObject(HttpURLConnection connection) throws IOException {
        String json = parseString(connection);
        return new JSONObject(json);
    }

    public static JSONObject parseJSONObjectAndDisconnect(HttpURLConnection connection) throws IOException {
        JSONObject jsonObject = parseJSONObject(connection);
        connection.disconnect();
        return jsonObject;
    }

    public static JSONArray parseJSONArray(HttpURLConnection connection) throws IOException {
        String json = parseString(connection);
        return new JSONArray(json);
    }

    public static JSONArray parseJSONArrayAndDisconnect(HttpURLConnection connection) throws IOException {
        JSONArray jsonArray = parseJSONArray(connection);
        connection.disconnect();
        return jsonArray;
    }
}

