import org.apache.kafka.clients.admin.*;
import java.util.*;

public class KafkaTopicManager {
    private AdminClient adminClient;

    public AdminClient createAndConfigureAdminClient(String bootstrapServers) {
        Properties props = new Properties();
        props.put(AdminClientConfig.BOOTSTRAP_SERVERS_CONFIG, bootstrapServers);
        return AdminClient.create(props);
    }

    public TopicDescription autoCreateMetricsTopic(AdminClient adminClient, String topicName) {
        this.adminClient = adminClient;
        NewTopic newTopic = new NewTopic(topicName, 1, (short) 1);
        adminClient.createTopics(Collections.singletonList(newTopic)).all().join();
        return adminClient.describeTopics(Collections.singletonList(topicName)).values().get(topicName).get();
    }
}

