import javax.net.ssl.*;
import java.io.*;
import java.net.HttpURLConnection;
import java.net.InetSocketAddress;
import java.net.Proxy;
import java.net.URL;
import java.security.KeyManagementException;
import java.security.NoSuchAlgorithmException;
import java.util.HashMap;
import java.util.Map;

public class HttpClient {
    private static Config config = Config.getInstance();

    static {
        try {
            SSLContext sslcontext = SSLContext.getInstance("SSL");
            sslcontext.init(null, new TrustManager[]{new MyX509TrustManager()}, null);
            HostnameVerifier ignoreHostnameVerifier = (s, sslsession) -> true;
            HttpsURLConnection.setDefaultHostnameVerifier(ignoreHostnameVerifier);
            HttpsURLConnection.setDefaultSSLSocketFactory(sslcontext.getSocketFactory());
        } catch (NoSuchAlgorithmException | KeyManagementException e) {
            e.printStackTrace();
        }
    }

    public static String sendRequest(HttpRequestInfo httpRequestInfo, String cookieValue) {
        HttpURLConnection connection = null;
        InputStream inputStream = null;
        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        try {
            connection = createConnection(httpRequestInfo);
            configureRequest(connection, httpRequestInfo, cookieValue);
            sendRequestBody(connection, httpRequestInfo);

            connection.setConnectTimeout(20000);
            connection.setReadTimeout(20000);
            connection.connect();

            inputStream = connection.getInputStream();
            if (inputStream.available() > 0) {
                byte[] buffer = new byte[inputStream.available()];
                inputStream.read(buffer);
                baos.write(buffer, 0, buffer.length);
            }
        } catch (Exception e) {
            return null;
        } finally {
            closeResources(connection, inputStream, baos);
        }
        return baos.toString();
    }

    private static HttpURLConnection createConnection(HttpRequestInfo httpRequestInfo) throws IOException {
        if (config.isProxyEnabled()) {
            Proxy proxy = new Proxy(Proxy.Type.HTTP, new InetSocketAddress(config.getProxyIP(), config.getProxyPort()));
            return (HttpURLConnection) new URL(httpRequestInfo.getRequestURL()).openConnection(proxy);
        } else {
            return (HttpURLConnection) new URL(httpRequestInfo.getRequestURL()).openConnection();
        }
    }

    private static void configureRequest(HttpURLConnection connection, HttpRequestInfo httpRequestInfo, String cookieValue) {
        connection.setRequestMethod(httpRequestInfo.getRequestMethod());
        Map<String, String> headers = new HashMap<>();
        if (httpRequestInfo.getHeaders().size() > 0) {
            headers.putAll(httpRequestInfo.getHeaders());
        } else {
            headers.put("User-Agent", "Mozilla/5.0 (Windows NT 10.0; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/85.0.4183.102 Safari/537.36");
        }
        configureCookie(headers, cookieValue);
        headers.forEach(connection::setRequestProperty);
        connection.setRequestProperty("connection", "close");
    }

    private static void configureCookie(Map<String, String> headers, String cookieValue) {
        String cookie = headers.get("Cookie");
        if (cookie == null) {
            headers.put("Cookie", config.getRememberMeCookieName() + "=" + cookieValue);
        } else if (!cookie.contains(config.getRememberMeCookieName() + "=")) {
            headers.put("Cookie", cookie + "; " + config.getRememberMeCookieName() + "=" + cookieValue);
        } else {
            int start = cookie.indexOf(config.getRememberMeCookieName()) + config.getRememberMeCookieName().length() + 1;
            int end = cookie.indexOf(";", start);
            end = end == -1 ? cookie.length() : end;
            headers.put("Cookie", cookie.substring(0, start) + cookieValue + cookie.substring(end));
        }
    }

    private static void sendRequestBody(HttpURLConnection connection, HttpRequestInfo httpRequestInfo) throws IOException {
        String requestBody = httpRequestInfo.getRequestBody();
        if (requestBody != null && !requestBody.trim().equals("")) {
            connection.setDoOutput(true);
            connection.setDoInput(true);
            PrintWriter printWriter = new PrintWriter(connection.getOutputStream());
            printWriter.write(requestBody);
            printWriter.flush();
        }
    }

    private static void closeResources(HttpURLConnection connection, InputStream inputStream, ByteArrayOutputStream baos) {
        try {
            if (connection != null) connection.disconnect();
            if (inputStream != null) inputStream.close();
            if (baos != null) baos.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}

