import java.io.*;
import java.net.*;
import java.security.cert.X509Certificate;
import javax.net.ssl.*;

public class HttpClient {
    private static Config config = new Config();

    public static String sendRequest(HttpRequestInfo httpRequestInfo, String cookieValue) {
        HttpURLConnection connection = null;
        InputStream inputStream = null;
        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        try {
            connection = createConnection(httpRequestInfo);
            configureRequest(connection, httpRequestInfo, cookieValue);
            sendRequestBody(connection, httpRequestInfo);
            int responseCode = connection.getResponseCode();
            if (responseCode == HttpURLConnection.HTTP_OK) {
                inputStream = connection.getInputStream();
            } else {
                inputStream = connection.getErrorStream();
            }
            byte[] buffer = new byte[1024];
            int bytesRead;
            while ((bytesRead = inputStream.read(buffer)) != -1) {
                baos.write(buffer, 0, bytesRead);
            }
            return baos.toString();
        } catch (IOException e) {
            e.printStackTrace();
            return null;
        } finally {
            closeResources(connection, inputStream, baos);
        }
    }

    private static HttpURLConnection createConnection(HttpRequestInfo httpRequestInfo) {
        try {
            URL url = new URL(httpRequestInfo.getUrl());
            HttpURLConnection connection = (HttpURLConnection) url.openConnection();
            if (config.getProxy() != null) {
                Proxy proxy = new Proxy(Proxy.Type.HTTP, new InetSocketAddress(config.getProxy().getHost(), config.getProxy().getPort()));
                connection = (HttpURLConnection) url.openConnection(proxy);
            }
            connection.setConnectTimeout(config.getConnectionTimeout());
            connection.setReadTimeout(config.getReadTimeout());
            if (!httpRequestInfo.isVerifySSL()) {
                trustAllHttpsCertificates();
                connection.setHostnameVerifier((hostname, session) -> true);
            }
            return connection;
        } catch (IOException e) {
            e.printStackTrace();
            return null;
        }
    }

    private static void configureRequest(HttpURLConnection connection, HttpRequestInfo httpRequestInfo, String cookieValue) {
        connection.setRequestMethod(httpRequestInfo.getMethod());
        configureHeaders(connection, httpRequestInfo);
        configureCookie(connection.getRequestProperties(), cookieValue);
    }

    private static void configureHeaders(HttpURLConnection connection, HttpRequestInfo httpRequestInfo) {
        for (Map.Entry<String, String> entry : httpRequestInfo.getHeaders().entrySet()) {
            connection.setRequestProperty(entry.getKey(), entry.getValue());
        }
    }

    private static void configureCookie(Map<String, String> headers, String cookieValue) {
        if (cookieValue != null && !cookieValue.isEmpty()) {
            headers.put("Cookie", cookieValue);
        }
    }

    private static void sendRequestBody(HttpURLConnection connection, HttpRequestInfo httpRequestInfo) {
        if (httpRequestInfo.getBody() != null && !httpRequestInfo.getBody().isEmpty()) {
            connection.setDoOutput(true);
            try (OutputStream os = connection.getOutputStream()) {
                os.write(httpRequestInfo.getBody().getBytes());
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private static void closeResources(HttpURLConnection connection, InputStream inputStream, ByteArrayOutputStream baos) {
        try {
            if (inputStream != null) inputStream.close();
            if (baos != null) baos.close();
            if (connection != null) connection.disconnect();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private static void trustAllHttpsCertificates() {
        try {
            TrustManager[] trustAllCerts = new TrustManager[]{new X509TrustManager() {
                public X509Certificate[] getAcceptedIssuers() {
                    return new X509Certificate[0];
                }

                public void checkClientTrusted(X509Certificate[] certs, String authType) {}

                public void checkServerTrusted(X509Certificate[] certs, String authType) {}
            }};

            SSLContext sc = SSLContext.getInstance("SSL");
            sc.init(null, trustAllCerts, new SecureRandom());
            HttpsURLConnection.setDefaultSSLSocketFactory(sc.getSocketFactory());
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    public static class Config {
        private String proxyHost;
        private int proxyPort;
        private String cookieName;
        private int connectionTimeout = 5000;
        private int readTimeout = 5000;

        public String getProxyHost() {
            return proxyHost;
        }

        public void setProxyHost(String proxyHost) {
            this.proxyHost = proxyHost;
        }

        public int getProxyPort() {
            return proxyPort;
        }

        public void setProxyPort(int proxyPort) {
            this.proxyPort = proxyPort;
        }

        public String getCookieName() {
            return cookieName;
        }

        public void setCookieName(String cookieName) {
            this.cookieName = cookieName;
        }

        public int getConnectionTimeout() {
            return connectionTimeout;
        }

        public void setConnectionTimeout(int connectionTimeout) {
            this.connectionTimeout = connectionTimeout;
        }

        public int getReadTimeout() {
            return readTimeout;
        }

        public void setReadTimeout(int readTimeout) {
            this.readTimeout = readTimeout;
        }

        public Proxy getProxy() {
            if (proxyHost == null || proxyPort <= 0) {
                return null;
            }
            return new Proxy(Proxy.Type.HTTP, new InetSocketAddress(proxyHost, proxyPort));
        }
    }

    public static class HttpRequestInfo {
        private String url;
        private String method = "GET";
        private Map<String, String> headers = new HashMap<>();
        private String body;
        private boolean verifySSL = true;

        public String getUrl() {
            return url;
        }

        public void setUrl(String url) {
            this.url = url;
        }

        public String getMethod() {
            return method;
        }

        public void setMethod(String method) {
            this.method = method;
        }

        public Map<String, String> getHeaders() {
            return headers;
        }

        public void setHeaders(Map<String, String> headers) {
            this.headers = headers;
        }

        public String getBody() {
            return body;
        }

        public void setBody(String body) {
            this.body = body;
        }

        public boolean isVerifySSL() {
            return verifySSL;
        }

        public void setVerifySSL(boolean verifySSL) {
            this.verifySSL = verifySSL;
        }
    }
}
