        String currentUrl = targetUrlString;
        for (int i = 0; i < MAXIMUM_REDIRECTS; i++) {
            Optional<String> nextUrl = getDownloadedOrNextUrl(currentUrl, path, userAgent);
            if (!nextUrl.isPresent()) {
                return;
