        logger.info("HTTP response code: {} for URL: {}", responseCode, urlString);

        if (responseCode >= 300 && responseCode < 400) {
            String location = connection.getHeaderField("Location");
            if (location == null) {
                throw new IOException("Received redirect response without Location header");
            }
            URL nextUrl = new URL(url, location);
            return Optional.of(nextUrl.toString());
