        }
    }

    public void processFile(Path filePath) throws IOException {
        try (FileChannel channel = FileChannel.open(filePath, StandardOpenOption.READ)) {
            long fileSize = channel.size();
            long chunkSize = 1024 * 1024;
            for (long position = 0; position < fileSize; position += chunkSize) {
                long remaining = fileSize - position;
                long mapSize = Math.min(chunkSize, remaining);
                MappedByteBuffer chunk = channel.map(FileChannel.MapMode.READ_ONLY, position, mapSize);
                processChunk(chunk, mapSize);
            }
        }
    }
}

