    }

    @Override
    public byte[] get(byte[] key) {
        return redis.get(key);
    }

    @Override
    public byte[] hget(byte[] key, byte[] field) {
        return redis.hget(key, field);
    }

    @Override
    public Map<CacheKeyTO, CacheWrapper<Object>> mget(Type type, Set<CacheKeyTO> keys) {
        Map<CacheKeyTO, CacheWrapper<Object>> result = new HashMap<>();
        ISerializer<Object> serializer = getSerializer();
        for (CacheKeyTO keyTO : keys) {
            byte[] keyBytes = keyTO.getKey();
            byte[] valueBytes = get(keyBytes);
            if (valueBytes != null) {
                try {
                    Object obj = serializer.deserialize(type, valueBytes);
                    if (obj instanceof CacheWrapper) {
                        result.put(keyTO, (CacheWrapper<Object>) obj);
                    }
                } catch (Exception e) {
                    log.error("Deserialization failed for key: {}", keyTO, e);
                }
            }
        }
        return result;
    }

    @Override
    public void delete(Set<CacheKeyTO> keys) {
        for (CacheKeyTO keyTO : keys) {
            byte[] keyBytes = keyTO.getKey();
            redis.del(keyBytes);
        }
    }
}

