import org.apache.rocketmq.client.consumer.DefaultMQPushConsumer;
import org.apache.rocketmq.client.consumer.MessageSelector;
import org.apache.rocketmq.client.consumer.listener.ConsumeConcurrentlyContext;
import org.apache.rocketmq.client.consumer.listener.ConsumeConcurrentlyStatus;
import org.apache.rocketmq.client.consumer.listener.MessageListenerConcurrently;
import org.apache.rocketmq.client.consumer.rebalance.AllocateMessageQueueAveragely;
import org.apache.rocketmq.client.exception.MQClientException;
import org.apache.rocketmq.common.MixAll;
import org.apache.rocketmq.common.filter.ExpressionType;
import org.apache.rocketmq.common.message.MessageExt;
import org.apache.rocketmq.remoting.RPCHook;

public class RocketMQConsumer {

    private DefaultMQPushConsumer consumer;
    private String topic;
    private String group;
    private int threadCount;
    private String filterType;
    private String expression;
    private boolean msgTraceEnable;
    private boolean aclEnable;
    private boolean clientRebalanceEnable;
    private RPCHook rpcHook;

    public RocketMQConsumer(String topic, String group, int threadCount, String filterType, 
                          String expression, boolean msgTraceEnable, boolean aclEnable, 
                          boolean clientRebalanceEnable, RPCHook rpcHook) {
        this.topic = topic;
        this.group = group;
        this.threadCount = threadCount;
        this.filterType = filterType;
        this.expression = expression;
        this.msgTraceEnable = msgTraceEnable;
        this.aclEnable = aclEnable;
        this.clientRebalanceEnable = clientRebalanceEnable;
        this.rpcHook = rpcHook;
    }

    public void initialize() throws MQClientException {
        consumer = new DefaultMQPushConsumer(group, rpcHook, new AllocateMessageQueueAveragely(), msgTraceEnable, null);
        consumer.setConsumeThreadMin(threadCount);
        consumer.setConsumeThreadMax(threadCount);
        consumer.setInstanceName(Long.toString(System.currentTimeMillis()));
        consumer.setClientRebalance(clientRebalanceEnable);

        if (filterType == null || expression == null) {
            consumer.subscribe(topic, "*");
        } else {
            if (ExpressionType.TAG.equals(filterType)) {
                String expr = MixAll.file2String(expression);
                consumer.subscribe(topic, MessageSelector.byTag(expr));
            } else if (ExpressionType.SQL92.equals(filterType)) {
                String expr = MixAll.file2String(expression);
                consumer.subscribe(topic, MessageSelector.bySql(expr));
            } else {
                throw new IllegalArgumentException("Not support filter type! " + filterType);
            }
        }
    }

    public void setMessageListener(MessageListenerConcurrently listener) {
        consumer.registerMessageListener(listener);
    }

    public void start() throws MQClientException {
        consumer.start();
    }

    public void shutdown() {
        consumer.shutdown();
    }

    public void setNamesrvAddr(String namesrvAddr) {
        consumer.setNamesrvAddr(namesrvAddr);
    }
}

