    private boolean returnFailedHalf;

    public RocketMQConsumer(String consumerGroup, String topic, String subscription, boolean returnFailedHalf) {
        this.consumerGroup = consumerGroup;
        this.topic = topic;
        this.subscription = subscription;
        this.returnFailedHalf = returnFailedHalf;
    }

    public void initialize() throws MQClientException {
        consumer = new DefaultMQPushConsumer(consumerGroup);
        consumer.setInstanceName(Long.toString(System.currentTimeMillis()));
        consumer.subscribe(topic, subscription != null ? subscription : "*");
    }

    public void start() throws MQClientException {
        consumer.registerMessageListener(new MessageListenerConcurrently() {
            AtomicLong consumeTimes = new AtomicLong(0);

            @Override
            public ConsumeConcurrentlyStatus consumeMessage(List<MessageExt> msgs,
                ConsumeConcurrentlyContext context) {
                long currentTimes = this.consumeTimes.incrementAndGet();
                System.out.printf("%-8d %s%n", currentTimes, msgs);
                if (returnFailedHalf) {
                    if ((currentTimes % 2) == 0) {
                        return ConsumeConcurrentlyStatus.RECONSUME_LATER;
                    }
                }
                return ConsumeConcurrentlyStatus.CONSUME_SUCCESS;
            }
        });

        consumer.start();
