    private String topic;
    private String tags;
    private String keys;

    public Producer(String group, String topic, String tags, String keys) throws MQClientException {
        this.group = group;
        this.topic = topic;
        this.tags = tags;
        this.keys = keys;
        this.producer = new DefaultMQProducer(group);
        this.producer.setInstanceName(Long.toString(System.currentTimeMillis()));
    }

    public void start() throws MQClientException {
        producer.start();
    }

    public SendResult sendMessage(String messageContent) throws Exception {
        Message msg = new Message(
            topic,
            tags,
            keys,
            messageContent.getBytes(RemotingHelper.DEFAULT_CHARSET));
        return producer.send(msg);
    }

    public void shutdown() {
        producer.shutdown();
    }

