            httpRequest.append("PUT /").append(configuration.getMountPoint()).append(" HTTP/1.1\r\n");
            Map<String, String> headers = getHTTPHeaders();
            for (Map.Entry<String, String> header : headers.entrySet()) {
                httpRequest.append(header.getKey()).append(": ").append(header.getValue()).append("\r\n");
            }
            httpRequest.append("\r\n");
            session.write(httpRequest.toString());
            mStreamingSession = session;
            mConnecting.set(false);
            setBroadcastState(BroadcastState.CONNECTED);
        }

        @Override
        public void sessionClosed(IoSession session) throws Exception {
            if (mStreamingSession == session) {
                mStreamingSession = null;
