    private NetServer server;

    public void startServer(Vertx vertx, int port, String host) {
        server = vertx.createNetServer();
        server.connectHandler(socket -> handleConnection(socket));
        server.listen(port, host, res -> {
            if (res.succeeded()) {
                System.out.println("Server started on " + host + ":" + port);
            } else {
                System.err.println("Failed to start server: " + res.cause());
            }
        });
    }

    public void handleConnection(NetSocket socket) {
        System.out.println("Client connected: " + socket.remoteAddress());

        socket.handler(buffer -> {
            socket.write(buffer);
        })
        .exceptionHandler(e -> {
            System.err.println("Connection error: " + e.getMessage());
        })
        .closeHandler(v -> {
            System.out.println("Client disconnected: " + socket.remoteAddress());
        });
    }

    public void stopServer() {
        if (server != null) {
            server.close(ar -> {
                if (ar.succeeded()) {
                    System.out.println("Server stopped successfully");
                } else {
                    System.err.println("Failed to stop server: " + ar.cause());
                }
            });
        }
    }
}

