public abstract class AbstractClient {
    protected final ConcurrentHashMap<String, ManagedChannel> chs = new ConcurrentHashMap<>();
    protected AbstractClientStubProxy stubProxy;
    protected final PDConfig config;
    protected ManagedChannel channel;
    protected final ConcurrentMap<String, AbstractBlockingStub> stubs = new ConcurrentHashMap<>();

    public AbstractClient(PDConfig config) {
        this.config = config;
    }

    public AbstractBlockingStub getBlockingStub() {
        String address = getCurrentLeaderAddress();
        return stubs.computeIfAbsent(address, k -> createBlockingStub());
    }

    public AbstractStub getStub() {
        String address = getCurrentLeaderAddress();
        return stubProxy.getAsyncStub(address);
    }

    protected abstract AbstractStub createStub();
    protected abstract AbstractBlockingStub createBlockingStub();

    public String resetStub() {
        if (channel != null) {
            closeChannel(channel);
        }
        String leaderHost = findCurrentLeaderHost();
        channel = chs.computeIfAbsent(leaderHost, host -> ManagedChannelBuilder
                .forTarget(host)
                .usePlaintext()
                .maxInboundMessageSize(config.getMaxMsgSize())
                .build());
        return leaderHost;
    }

    public <ReqT, RespT> RespT blockingUnaryCall(MethodDescriptor<ReqT, RespT> method, ReqT req) {
        return blockingUnaryCall(method, req, config.getMaxRetries());
    }

    public <ReqT, RespT> RespT blockingUnaryCall(MethodDescriptor<ReqT, RespT> method, ReqT req, int retry) {
        AtomicReference<StatusRuntimeException> lastEx = new AtomicReference<>();
        for (int i = 0; i < retry; i++) {
            try {
                return getBlockingStub().blockingUnaryCall(
                        channel.newCall(method, CallOptions.DEFAULT),
                        req);
            } catch (StatusRuntimeException e) {
                lastEx.set(e);
                if (e.getStatus() == Status.UNAVAILABLE && i < retry - 1) {
                    resetStub();
                    try { Thread.sleep(config.getRetryBackoff()); }
                    catch (InterruptedException ie) { Thread.currentThread().interrupt(); }
                }
            }
        }
        throw lastEx.get();
    }

    public <ReqT, RespT> KVPair<Boolean, RespT> concurrentBlockingUnaryCall(
            MethodDescriptor<ReqT, RespT> method, ReqT req, Predicate<RespT> predicate) {
        ExecutorService executor = Executors.newFixedThreadPool(config.getMaxConcurrent());
        List<CompletableFuture<KVPair<Boolean, RespT>>> futures = stubs.values().stream()
                .map(stub -> CompletableFuture.supplyAsync(() -> {
                    try {
                        RespT resp = stub.blockingUnaryCall(
                                channel.newCall(method, CallOptions.DEFAULT),
                                req);
                        return new KVPair<>(predicate.test(resp), resp);
                    } catch (Exception e) {
                        return new KVPair<>(false, null);
                    }
                }, executor))
                .collect(Collectors.toList());

        CompletableFuture<Void> allDone = CompletableFuture.allOf(
                futures.toArray(new CompletableFuture[0]));
        return allDone.thenApply(v -> futures.stream()
                .map(f -> f.join())
                .filter(KVPair::getKey)
                .findFirst()
                .orElse(new KVPair<>(false, null))).join();
    }

    public <ReqT, RespT> void streamingCall(MethodDescriptor<ReqT, RespT> method,
                                            ReqT request, StreamObserver<RespT> responseObserver, int retry) {
        AbstractStub stub = getStub()
                .withDeadlineAfter(config.getTimeout(), TimeUnit.MILLISECONDS)
                .withCallCredentials(new RetryCallCredentials(retry));
        stub.serverStreamingCall(request, responseObserver);
    }

    public void close() {
        chs.forEach((host, ch) -> closeChannel(ch));
        stubs.clear();
    }

    protected void closeChannel(ManagedChannel channel) {
        if (channel != null && !channel.isShutdown()) {
            try {
                channel.shutdown().awaitTermination(5, TimeUnit.SECONDS);
            } catch (InterruptedException e) {
                channel.shutdownNow();
                Thread.currentThread().interrupt();
            }
        }
    }

    private String getCurrentLeaderAddress() {
        return channel.getTarget();
    }

    private String findCurrentLeaderHost() {
        // Implementation to discover current leader
        return config.getHosts().get(0);
    }

    private static class RetryCallCredentials extends CallCredentials {
        private final int maxRetries;
        private RetryCallCredentials(int maxRetries) {
            this.maxRetries = maxRetries;
        }
        @Override
        public void applyRequestMetadata(RequestInfo requestInfo, Executor executor, MetadataApplier applier) {
            applier.apply(new Metadata());
        }
        @Override
        public void thisUsesUnstableApi() {}
    }
}

