            } catch (Exception e) {
                logger.log("Server error: " + e.getMessage());
            } finally {
                try {
                    serverSocket.close();
                } catch (IOException e) {
                    logger.log("Error closing server socket: " + e.getMessage());
                }
            }
        }

        private void readExpectedCommand(ExpectedCommand expected, BufferedReader in) throws IOException {
            String line = in.readLine();
            if (line == null) {
                throw new IOException("Client disconnected unexpectedly");
            }
            logger.log("Received: " + line);
            if (!line.trim().equals(expected.getCommand().trim())) {
                throw new AssertionError("Expected command: " + expected.getCommand() + ", but got: " + line);
            }
        }

        private void writeCannedResponse(CannedResponse response, PrintWriter out) {
            out.println(response.getResponse());
            logger.log("Sent: " + response.getResponse());
        }

        private Socket upgradeToTls(Socket socket) throws Exception {
            KeyStore keyStore = keyStoreProvider.loadKeyStore();
            KeyManagerFactory kmf = KeyManagerFactory.getInstance(KeyManagerFactory.getDefaultAlgorithm());
            kmf.init(keyStore, keyStoreProvider.getKeyStorePassword().toCharArray());

            SSLContext sslContext = SSLContext.getInstance("TLS");
            sslContext.init(kmf.getKeyManagers(), null, null);

            SSLSocketFactory socketFactory = sslContext.getSocketFactory();
            SSLSocket sslSocket = (SSLSocket) socketFactory.createSocket(
                socket, socket.getInetAddress().getHostName(), socket.getPort(), true);
            sslSocket.setUseClientMode(false);
            sslSocket.startHandshake();

            logger.log("Upgraded connection to TLS");
            return sslSocket;
        }

        public void shouldStop() {
            stop = true;
            interrupt();
        }
    }
