import java.net.URLEncoder;

public class HttpFeedbackClient {

    private String content;
    private String env;
    private OnRequestCallback callback;

    public HttpFeedbackClient(String content, String env, OnRequestCallback callback) {
        this.content = content;
        this.env = env;
        this.callback = callback;
    }

    public void sendFeedback() {
        try {
            URL url = new URL("https://www.freelinebuild.com/api/feedback");
            HttpURLConnection conn = (HttpURLConnection) url.openConnection();
            conn.setReadTimeout(10000);
            conn.setConnectTimeout(15000);
            conn.setRequestMethod("POST");
            conn.setDoInput(true);
            conn.setDoOutput(true);

            StringBuilder builder = new StringBuilder();
            builder.append(URLEncoder.encode("content", "UTF-8"));
            builder.append("=");
            builder.append(URLEncoder.encode(content, "UTF-8"));
            builder.append("&");
            builder.append(URLEncoder.encode("env", "UTF-8"));
            builder.append("=");
            builder.append(URLEncoder.encode(env, "UTF-8"));

            OutputStream os = conn.getOutputStream();
            BufferedWriter writer = new BufferedWriter(new OutputStreamWriter(os, "UTF-8"));
            writer.write(builder.toString());
            writer.flush();
            writer.close();
            os.close();

            int responseCode = conn.getResponseCode();
            if (responseCode >= 400) {
                this.callback.onFailure(new Exception(conn.getResponseMessage()));
            } else {
                this.callback.onSuccess();
            }
            conn.disconnect();
        } catch (IOException e) {
            this.callback.onFailure(e);
        }
    }
}

