import android.content.Context;
import android.util.Log;

import com.trianguloy.urlchecker.utilities.methods.HttpUtils;

import java.io.IOException;
import java.net.HttpURLConnection;
import java.net.URL;

public class HttpStatusChecker {

    private final Context context;
    private final String url;

    public HttpStatusChecker(Context context, String url) {
        this.context = context;
        this.url = url;
    }

    public StatusResult checkStatus() {
        String message = null;
        String redirectionUrl = null;

        HttpURLConnection conn = null;
        try {
            // Step 2: Get the access URL
            Log.d("STATUS", "Checking: " + url);

            // Step 3: Create an HttpURLConnection object
            conn = (HttpURLConnection) new URL(url).openConnection();

            // Step 4: Set request parameters
            conn.setInstanceFollowRedirects(false);
            conn.setConnectTimeout(HttpUtils.CONNECT_TIMEOUT);

            // Step 5: Processing Input and Output
            var responseCode = conn.getResponseCode();
            Log.d("RESPONSE_CODE", url + ": " + responseCode);

            var codesArray = context.getResources().getStringArray(R.array.mStatus_codes);
            for (var s : codesArray) {
                if (s.startsWith(String.valueOf(responseCode))) {
                    message = s;
                    break;
                }
            }
            if (message == null) {
                message = context.getString(R.string.mStatus_unknownCode, responseCode);
            }

            var location = conn.getHeaderField("Location");
            if (location != null) {
                redirectionUrl = new URL(new URL(url), location).toExternalForm();
            }
        } catch (IOException e) {
            e.printStackTrace();
            message = context.getString(R.string.mStatus_ioerror, e.getMessage());
        } catch (Exception e) {
            e.printStackTrace();
            message = context.getString(R.string.mStatus_error, e.getMessage());
        } finally {
            // Step 6: Disconnect
            if (conn != null) {
                conn.disconnect();
            }
        }

        return new StatusResult(message, redirectionUrl);
    }

    public static class StatusResult {
        public final String message;
        public final String redirectionUrl;

        public StatusResult(String message, String redirectionUrl) {
            this.message = message;
            this.redirectionUrl = redirectionUrl;
        }
    }
}

