            conn.setDoOutput(true);
            setHeaders(conn, headers, encoding);

            String encodedParams = encodingParams(paramValues, encoding);
            OutputStream os = conn.getOutputStream();
            os.write(encodedParams.getBytes(encoding));
            os.flush();
            os.close();

            int code = conn.getResponseCode();
            InputStream is;
            try {
                is = conn.getInputStream();
            } catch (IOException e) {
                is = conn.getErrorStream();
            }
            String content = readStream(is, encoding);
            return new HttpResult(code, content);
        } catch (Exception e) {
            return new HttpResult(0, "");
        }
    }

    private static String readStream(InputStream is, String encoding) {
        if (is == null) {
            return "";
        }
        StringBuilder sb = new StringBuilder();
        try (BufferedReader reader = new BufferedReader(new InputStreamReader(is, encoding))) {
            String line;
            while ((line = reader.readLine()) != null) {
                sb.append(line);
            }
        } catch (IOException e) {
            return "";
        }
        return sb.toString();
    }
}

