        @Override
        public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) {
            ctx.channel().attr(FUTURE_KEY).get().completeExceptionally(cause);
            ctx.close();
        }
    }

    static class TimeoutHandler extends ChannelInboundHandlerAdapter {
        private final int timeout;
        private final CompletableFuture<String> future;
        private ScheduledFuture<?> scheduledFuture;

        public TimeoutHandler(int timeout, CompletableFuture<String> future) {
            this.timeout = timeout;
            this.future = future;
        }

        @Override
        public void channelActive(ChannelHandlerContext ctx) {
            scheduledFuture = ctx.channel().eventLoop().schedule(() -> {
                if (!future.isDone()) {
                    future.completeExceptionally(new RuntimeException("Request timed out after " + timeout + " ms"));
                }
            }, timeout, TimeUnit.MILLISECONDS);
        }

        @Override
        public void channelRead(ChannelHandlerContext ctx, Object msg) {
            if (future.isDone()) {
                if (scheduledFuture != null) {
                    scheduledFuture.cancel(false);
                }
            }
            ctx.fireChannelRead(msg);
        }

        @Override
        public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) {
            future.completeExceptionally(cause);
            ctx.close();
