import io.netty.channel.socket.nio.NioSocketChannel;
import io.netty.handler.codec.string.StringEncoder;

import java.util.Map;
import java.util.HashMap;

public class TcpClientManager {
    private final Map<String, Channel> channelMap = new HashMap<>();
    private final Map<String, NioEventLoopGroup> groupMap = new HashMap<>();

    public Channel initSocketClientAndSendMessage(String host, String message) {
        String[] parts = host.split(":");
        String hostAddress = parts[0];
        int port = Integer.parseInt(parts[1]);

        Channel channel = channelMap.get(host);
        if (channel != null && channel.isActive()) {
            channel.writeAndFlush(message);
            return channel;
        }

        NioEventLoopGroup group = groupMap.get(host);
        if (group == null) {
            group = new NioEventLoopGroup();
            groupMap.put(host, group);
        }

        Bootstrap bootstrap = new Bootstrap();
        bootstrap.group(group)
                .channel(NioSocketChannel.class)
                .handler(new ChannelInitializer<SocketChannel>() {
                    @Override
                    protected void initChannel(SocketChannel ch) {
                        ch.pipeline().addLast(new StringEncoder(), new ClientHandler(message));
                    }
                });

        ChannelFuture future = bootstrap.connect(hostAddress, port).sync();
        channel = future.channel();
        channelMap.put(host, channel);
        return channel;
    }

    private static class ClientHandler extends ChannelInboundHandlerAdapter {
        private final String message;

        public ClientHandler(String message) {
            this.message = message;
        }

        @Override
        public void channelActive(ChannelHandlerContext ctx) {
            ctx.writeAndFlush(message);
        }
    }
}

