        return socket != null && socket.isConnected() && !broken;
    }

    public boolean isBroken() {
        return broken;
    }

    public void setBroken() {
        this.broken = true;
    }

    public void sendCommand(ProtocolCommand command, byte[]... args) throws IOException {
        outputStream.writeCommand(command, args);
        outputStream.flush();
    }

    public Object getOne() throws IOException {
        return inputStream.readObject();
    }

    public void flush() throws IOException {
        outputStream.flush();
    }

    public Object readProtocolWithCheckingBroken() throws IOException {
        if (broken) {
            throw new JedisConnectionException("Connection is broken");
        }
        return getOne();
    }

    @Override
    public void close() {
        disconnect();
    }
}
