    private long mDataChunkSizeOffset;
    private int mDataChunkSize;

    public WaveWriter(AudioFormat format, Path file, long maxSize) throws IOException {
        this.mAudioFormat = Objects.requireNonNull(format);
        this.mFile = Objects.requireNonNull(file);
        this.mMaxSize = maxSize;
        this.mFileRolloverCounter = 0;
        open();
    }

    public WaveWriter(AudioFormat format, Path file) throws IOException {
        this(format, file, 0);
    }

    private void open() throws IOException {
        Files.createDirectories(mFile.getParent());
        mFileChannel = FileChannel.open(mFile, StandardOpenOption.CREATE, StandardOpenOption.WRITE, StandardOpenOption.TRUNCATE_EXISTING);
        ByteBuffer header = getWaveHeader(mAudioFormat);
        mFileChannel.write(header);
        mDataChunkSizeOffset = mFileChannel.position() - 4;
        mDataChunkOpen = true;
        mDataChunkSize = 0;
    }

    @Override
    public void close() throws IOException {
        if (mFileChannel != null) {
            closeDataChunk();
            updateTotalSize();
            mFileChannel.close();
            mFileChannel = null;
        }
    }

    public void close(Path path) throws IOException {
        close();
        Files.move(mFile, path, StandardCopyOption.REPLACE_EXISTING);
    }

    public void writeData(ByteBuffer buffer) throws IOException {
        if (mMaxSize > 0 && (mFileChannel.size() + buffer.remaining()) > mMaxSize) {
            rollover();
        }
        mFileChannel.write(buffer);
        mDataChunkSize += buffer.remaining();
    }

    private void closeDataChunk() throws IOException {
        if (mDataChunkOpen) {
            updateDataChunkSize();
            mDataChunkOpen = false;
        }
    }

    private void openDataChunk() throws IOException {
        if (!mDataChunkOpen) {
            mDataChunkOpen = true;
            mDataChunkSize = 0;
        }
    }

    private void rollover() throws IOException {
        close();
        Path originalFile = mFile;
        String fileName = originalFile.getFileName().toString();
        int dotIndex = fileName.lastIndexOf('.');
        String baseName = fileName.substring(0, dotIndex);
        String extension = fileName.substring(dotIndex);
        String newFileName = baseName + "_" + mFileRolloverCounter + extension;
        Path newFilePath = originalFile.resolveSibling(newFileName);
        Files.move(originalFile, newFilePath, StandardCopyOption.REPLACE_EXISTING);
        mFileRolloverCounter++;
        mFile = originalFile;
        open();
    }

    private void updateTotalSize() throws IOException {
        long fileSize = mFileChannel.size();
        long totalSize = fileSize - 8;
        mFileChannel.position(4);
        ByteBuffer sizeBuffer = getUnsignedIntegerBuffer(totalSize);
        mFileChannel.write(sizeBuffer);
    }

    private void updateDataChunkSize() throws IOException {
        mFileChannel.position(mDataChunkSizeOffset);
        ByteBuffer sizeBuffer = getUnsignedIntegerBuffer(mDataChunkSize);
        mFileChannel.write(sizeBuffer);
    }

    protected static ByteBuffer getUnsignedIntegerBuffer(long size) {
        ByteBuffer buffer = ByteBuffer.allocate(4);
        buffer.order(ByteOrder.LITTLE_ENDIAN);
        buffer.putInt((int) size);
        return buffer;
    }

    public static ByteBuffer getDataHeader() {
        ByteBuffer dataHeader = ByteBuffer.allocate(8);
        dataHeader.order(ByteOrder.LITTLE_ENDIAN);
        dataHeader.put("data".getBytes());
        dataHeader.putInt(0);
        return dataHeader;
    }

    public static ByteBuffer getWaveHeader(AudioFormat format) {
        ByteBuffer header = ByteBuffer.allocate(36);
        header.order(ByteOrder.LITTLE_ENDIAN);
        header.put("RIFF".getBytes());
        header.putInt(0);
        header.put("WAVE".getBytes());
        header.put("fmt ".getBytes());
        header.putInt(16);
        header.putShort((short) 1);
        header.putShort((short) format.getChannels());
        header.putInt(format.getSampleRate());
        header.putInt(format.getSampleRate() * format.getChannels() * (format.getSampleSizeInBits() / 8));
        header.putShort((short) (format.getChannels() * (format.getSampleSizeInBits() / 8)));
        header.putShort((short) format.getSampleSizeInBits());
        header.put("data".getBytes());
        header.putInt(0);
        return header;
    }

    public static ByteBuffer getFormatChunk(AudioFormat format) {
        ByteBuffer formatChunk = ByteBuffer.allocate(16);
        formatChunk.order(ByteOrder.LITTLE_ENDIAN);
        formatChunk.put("fmt ".getBytes());
        formatChunk.putInt(16);
        formatChunk.putShort((short) 1);
        formatChunk.putShort((short) format.getChannels());
        formatChunk.putInt(format.getSampleRate());
        formatChunk.putInt(format.getSampleRate() * format.getChannels() * (format.getSampleSizeInBits() / 8));
        formatChunk.putShort((short) (format.getChannels() * (format.getSampleSizeInBits() / 8)));
        formatChunk.putShort((short) format.getSampleSizeInBits());
        return formatChunk;
    }
}
