import redis.clients.jedis.HostAndPort;
import redis.clients.jedis.Jedis;
import redis.clients.jedis.JedisClientConfig;
import redis.clients.jedis.util.Hashing;
import java.util.List;

public class JedisSharding implements AutoCloseable {

    private final List<HostAndPort> shards;
    private final JedisClientConfig clientConfig;
    private final Hashing hashing;

    public JedisSharding(List<HostAndPort> shards, JedisClientConfig clientConfig) {
        this(shards, clientConfig, Hashing.MURMUR_HASH);
    }

    public JedisSharding(List<HostAndPort> shards, JedisClientConfig clientConfig, Hashing hashing) {
        this.shards = shards;
        this.clientConfig = clientConfig;
        this.hashing = hashing;
    }

    public String set(String key, String value) {
        try (Jedis jedis = getShard(key)) {
            return jedis.set(key, value);
        }
    }

    public String get(String key) {
        try (Jedis jedis = getShard(key)) {
            return jedis.get(key);
        }
    }

    public Object sendCommand(String key, Protocol.Command command, String... args) {
        try (Jedis jedis = getShard(key)) {
            return jedis.sendCommand(command, args);
        }
    }

    private Jedis getShard(String key) {
        int shardIndex = getShardIndex(key);
        HostAndPort shard = shards.get(shardIndex);
        Jedis jedis = new Jedis(shard, clientConfig);
        if (shard.getPassword() != null) {
            jedis.auth(shard.getPassword());
        }
        return jedis;
    }

    private int getShardIndex(String key) {
        long hash = hashing.hash(key);
        return (int) (hash % shards.size());
    }

    @Override
    public void close() {
        // Connection cleanup handled by try-with-resources in individual operations
    }
}

