import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.nio.file.Files;

public class PathResource {
    private final Path file;
    private final PathResourceManager manager;

    public PathResource(Path file, PathResourceManager manager) {
        this.file = file;
        this.manager = manager;
    }

    public void serve(Sender sender, HttpServerExchange exchange, IoCallback callback) {
        serveImpl(sender, exchange, 0, getContentLength() - 1, callback, false);
    }

    public void serveRange(Sender sender, HttpServerExchange exchange, long start, long end, IoCallback callback) {
        serveImpl(sender, exchange, start, end, callback, true);
    }

    private void serveImpl(Sender sender, HttpServerExchange exchange, long start, long end, IoCallback callback, boolean isRange) {
        Long contentLength = getContentLength();
        if (contentLength == null) {
            callback.onError(new IOException("File not found"));
            return;
        }

        if (isRange && (start > end || start >= contentLength)) {
            callback.onError(new IOException("Invalid range"));
            return;
        }

        try (FileChannel channel = FileChannel.open(file, StandardOpenOption.READ)) {
            if (isRange) {
                channel.position(start);
                long length = end - start + 1;
                if (manager.getTransferSizeThreshold() < length) {
                    new TransferTask(channel, sender, exchange, start, end, callback).run();
                } else {
                    new ServerTask(channel, sender, exchange, start, end, callback).run();
                }
            } else {
                long length = contentLength;
                if (manager.getTransferSizeThreshold() < length) {
                    new TransferTask(channel, sender, exchange, 0, contentLength - 1, callback).run();
                } else {
                    new ServerTask(channel, sender, exchange, 0, contentLength - 1, callback).run();
                }
            }
        } catch (IOException e) {
            callback.onError(e);
        }
    }

    private Long getContentLength() {
        try {
            return Files.size(file);
        } catch (IOException e) {
            return null;
        }
    }

    private abstract class BaseFileTask {
        protected final FileChannel channel;
        protected final Sender sender;
        protected final HttpServerExchange exchange;
        protected final long start;
        protected final long end;
        protected final IoCallback callback;

        protected BaseFileTask(FileChannel channel, Sender sender, HttpServerExchange exchange, long start, long end, IoCallback callback) {
            this.channel = channel;
            this.sender = sender;
            this.exchange = exchange;
            this.start = start;
            this.end = end;
            this.callback = callback;
        }

        abstract void run();
    }

    private class ServerTask extends BaseFileTask {
        private final int bufferSize;

        public ServerTask(FileChannel channel, Sender sender, HttpServerExchange exchange, long start, long end, IoCallback callback) {
            super(channel, sender, exchange, start, end, callback);
            this.bufferSize = manager.getBufferSize();
        }

        @Override
        void run() {
            try {
                long remaining = end - start + 1;
                ByteBuffer buffer = PooledByteBuffer.allocate(bufferSize);
                while (remaining > 0) {
                    int read = channel.read(buffer, start);
                    if (read <= 0) {
                        break;
                    }
                    buffer.flip();
                    sender.send(buffer, callback);
                    buffer.clear();
                    remaining -= read;
                }
                callback.onComplete();
            } catch (IOException e) {
                callback.onError(e);
            } finally {
                PooledByteBuffer.release(buffer);
            }
        }
    }

    private class TransferTask extends BaseFileTask {
        public TransferTask(FileChannel channel, Sender sender, HttpServerExchange exchange, long start, long end, IoCallback callback) {
            super(channel, sender, exchange, start, end, callback);
        }

        @Override
        void run() {
            try {
                long length = end - start + 1;
                channel.position(start);
                channel.transferTo(start, length, sender);
                callback.onComplete();
            } catch (IOException e) {
                callback.onError(e);
            }
        }
    }
}
