        handler.post(() -> {
            try {
                if (socketClient != null && !socketClient.isClosed()) {
                    OutputStream out = socketClient.getOutputStream();
                    out.write(data);
                    out.flush();
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        });
    }

    public void onReceive(ReceiveListener receiveListener) {
        this.receiveListener = receiveListener;
    }

    public void destroy() {
        closeAll();
    }

    private void closeAll() {
        try {
            if (socketClient != null && !socketClient.isClosed()) {
                socketClient.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
        if (receiveThread != null) {
            receiveThread.interrupt();
        }
        if (handler != null) {
            handler.removeCallbacksAndMessages(null);
        }
    }

    public void startReceive() {
        if (receiveThread == null) {
            receiveThread = new Thread(new Receiver());
        }
        if (!receiveThread.isAlive()) {
            receiveThread.start();
        }
    }

    private int getLengthFromHeadByte(byte[] b) {
        return ((b[4] & 0xFF) << 24) | ((b[5] & 0xFF) << 16) | ((b[6] & 0xFF) << 8) | (b[7] & 0xFF);
    }

    private int getTypeFromHeadByte(byte[] b) {
        return ((b[0] & 0xFF) << 24) | ((b[1] & 0xFF) << 16) | ((b[2] & 0xFF) << 8) | (b[3] & 0xFF);
    }

    private class Receiver implements Runnable {
        @Override
        public void run() {
            try {
                InputStream in = socketClient.getInputStream();
                byte[] header = new byte[PACKAGE_HEAD_LENGTH];
                while (!socketClient.isClosed()) {
                    int bytesRead = 0;
                    while (bytesRead < PACKAGE_HEAD_LENGTH) {
                        int read = in.read(header, bytesRead, PACKAGE_HEAD_LENGTH - bytesRead);
                        if (read == -1) {
                            break;
                        }
                        bytesRead += read;
                    }
                    if (bytesRead != PACKAGE_HEAD_LENGTH) {
                        break;
                    }
                    int type = getTypeFromHeadByte(header);
                    int length = getLengthFromHeadByte(header);
                    byte[] body = new byte[length];
                    bytesRead = 0;
                    while (bytesRead < length) {
                        int read = in.read(body, bytesRead, length - bytesRead);
                        if (read == -1) {
                            break;
                        }
                        bytesRead += read;
                    }
                    if (bytesRead != length) {
                        break;
                    }
                    if (receiveListener != null) {
                        receiveListener.onReceive(body);
                    }
                }
            } catch (IOException e) {
                e.printStackTrace();
            } finally {
                closeAll();
            }
        }
    }

    public interface ReceiveListener {
        void onReceive(byte[] data);
    }
}
