                if (responseCode >= 300 && responseCode < 400) {
                    String location = conn.getHeaderField("Location");
                    if (location == null) {
                        return false;
                    }
                    if (location.startsWith("/")) {
                        String base = currentUrl.substring(0, currentUrl.lastIndexOf('/') + 1);
                        currentUrl = base + location;
                    } else {
                        currentUrl = location;
                    }
                    redirectCount++;
                    continue;
                } else {
                    break;
                }
            }

            String host = url2host(currentUrl);
            if (host.isEmpty()) {
                return false;
            }
            String ip = host2ip(host);
            if (ip.isEmpty()) {
                return false;
            }
            return !isInternalIp(ip);
        } catch (Exception e) {
            logger.error("SSRF check failed", e);
            return false;
        }
    }

    private boolean isInternalIpByUrl(String url) {
        String host = url2host(url);
        if (host.isEmpty()) {
            return false;
        }
        String ip = host2ip(host);
        if (ip.isEmpty()) {
            return false;
        }
        return isInternalIp(ip);
    }

    private boolean isInternalIp(String strIP) {
        try {
            InetAddress ip = InetAddress.getByName(strIP);
            byte[] bytes = ip.getAddress();
            long ipLong = 0;
            for (byte b : bytes) {
                ipLong = (ipLong << 8) | (b & 0xFF);
            }

            long net10 = (10L << 24) | 0;
            long mask10 = 0xFF000000L;
            if ((ipLong & mask10) == net10) {
                return true;
            }

            long net172 = (172L << 24) | (16L << 16);
            long mask172 = 0xFFF00000L;
            if ((ipLong & mask172) == net172) {
                return true;
            }

            long net192 = (192L << 24) | (168L << 16);
            long mask192 = 0xFFFF0000L;
            if ((ipLong & mask192) == net192) {
                return true;
            }

            long net127 = (127L << 24) | 0;
            long mask127 = 0xFF000000L;
            if ((ipLong & mask127) == net127) {
                return true;
            }

            return false;
        } catch (UnknownHostException e) {
            return false;
        }
    }

    private String host2ip(String host) {
        try {
            InetAddress addr = InetAddress.getByName(host);
            return addr.getHostAddress();
        } catch (UnknownHostException e) {
            return "";
        }
    }

    private String url2host(String url) {
        try {
            URL u = new URL(url);
            if (!u.getProtocol().equals("http") && !u.getProtocol().equals("https")) {
                return "";
            }
            return u.getHost();
        } catch (MalformedURLException e) {
            return "";
        }
    }

    private boolean isOctalIP(String host) {
        String[] parts = host.split("\\.");
        if (parts.length != 4) {
            return false;
        }
        for (String part : parts) {
            if (part.isEmpty()) {
                return false;
            }
            if (part.charAt(0) != '0') {
                return false;
            }
            try {
                Integer.parseInt(part, 8);
            } catch (NumberFormatException e) {
                return false;
            }
        }
        return true;
    }
}
