import javax.jms.*;
import org.apache.activemq.ActiveMQConnectionFactory;

public class ActiveMqConsumer implements Runnable, ExceptionListener {
    private Connection connection;
    private Session session;
    private MessageConsumer consumer;
    private String remoteUri;
    private String queueName;
    private volatile boolean running = false;

    public ActiveMqConsumer(String remoteUri, String queueName) {
        this.remoteUri = remoteUri;
        this.queueName = queueName;
    }

    public void start() {
        try {
            ConnectionFactory factory = new ActiveMQConnectionFactory(remoteUri);
            connection = factory.createConnection();
            connection.setExceptionListener(this);
            connection.start();
            session = connection.createSession(false, Session.AUTO_ACKNOWLEDGE);
            Destination queue = session.createQueue(queueName);
            consumer = session.createConsumer(queue);
            running = true;
        } catch (JMSException e) {
            e.printStackTrace();
        }
    }

    public void stop() {
        running = false;
        try {
            if (consumer != null) consumer.close();
            if (session != null) session.close();
            if (connection != null) connection.close();
        } catch (JMSException e) {
            e.printStackTrace();
        }
    }

    public void run() {
        while (running) {
            try {
                Message message = consumer.receive(1000);
                if (message == null) continue;
                if (message instanceof TextMessage) {
                    TextMessage textMsg = (TextMessage) message;
                    String text = textMsg.getText();
                    System.out.println("Received TextMessage: " + text);
                } else if (message instanceof BytesMessage) {
                    BytesMessage bytesMsg = (BytesMessage) message;
                    long length = bytesMsg.getBodyLength();
                    byte[] buffer = new byte[(int) length];
                    bytesMsg.readBytes(buffer);
                    System.out.println("Received BytesMessage of length " + buffer.length);
                } else {
                    System.out.println("Unknown message type: " + message.getClass().getName());
                }
            } catch (JMSException e) {
                e.printStackTrace();
                break;
            }
        }
    }

    public void onException(JMSException e) {
        System.err.println("JMS Exception occurred: " + e.getMessage());
        stop();
    }

    public Connection getConnection() {
        return connection;
    }

    public Session getSession() {
        return session;
    }

    public MessageConsumer getConsumer() {
        return consumer;
    }
}
