public class HttpDebugIdSender {
    private String distinctId;
    private String infoId;
    private String serverUrl;

    public HttpDebugIdSender(String serverUrl, String distinctId, String infoId) {
        this.distinctId = distinctId;
        this.infoId = infoId;
        this.serverUrl = serverUrl;
    }

    public void send() {
        sendHttpRequest(serverUrl, false);
    }

    private void sendHttpRequest(String serverUrl, boolean isRedirects) {
        ByteArrayOutputStream out = null;
        OutputStream out2 = null;
        BufferedOutputStream bout = null;
        HttpURLConnection connection = null;
        try {
            URL url = new URL(String.format(serverUrl + "&info_id=%s", infoId));
            SALog.info("HttpDebugIdSender", String.format("DebugMode URL:%s", url), null);
            connection = (HttpURLConnection) url.openConnection();
            if (connection == null) {
                SALog.info("HttpDebugIdSender", String.format("can not connect %s,shouldn't happen", url.toString()), null);
                return;
            }
            SAConfigOptions configOptions = SensorsDataAPI.getConfigOptions();
            if (configOptions != null && configOptions.getSSLSocketFactory() != null
                    && connection instanceof HttpsURLConnection) {
                ((HttpsURLConnection) connection).setSSLSocketFactory(configOptions.getSSLSocketFactory());
            }
            connection.setInstanceFollowRedirects(false);
            out = new ByteArrayOutputStream();
            OutputStreamWriter writer = new OutputStreamWriter(out);
            String requestBody = "{\"distinct_id\": \"" + distinctId + "\"}";
            writer.write(requestBody);
            writer.flush();
            SALog.info("HttpDebugIdSender", String.format("DebugMode request body : %s", requestBody), null);
            connection.setDoOutput(true);
            connection.setUseCaches(false);
            connection.setRequestMethod("POST");
            connection.setRequestProperty("Content-type", "text/plain");

            out2 = connection.getOutputStream();
            bout = new BufferedOutputStream(out2);
            bout.write(out.toString().getBytes("UTF-8"));
            bout.flush();
            out.close();
            int responseCode = connection.getResponseCode();
            SALog.info("HttpDebugIdSender", String.format(TimeUtils.SDK_LOCALE, "DebugMode ResponseCode: %d", responseCode), null);
            if (!isRedirects && NetworkUtils.needRedirects(responseCode)) {
                String location = NetworkUtils.getLocation(connection, serverUrl);
                if (!TextUtils.isEmpty(location)) {
                    closeStream(out, out2, bout, connection);
                    sendHttpRequest(location, true);
                }
            }
        } catch (Exception e) {
            SALog.printStackTrace(e);
        } finally {
            closeStream(out, out2, bout, connection);
        }
    }

    private void closeStream(ByteArrayOutputStream out, OutputStream out2, BufferedOutputStream bout, HttpURLConnection connection) {
        if (out != null) {
            try {
                out.close();
            } catch (Exception e) {
                SALog.printStackTrace(e);
            }
        }
        if (out2 != null) {
            try {
                out2.close();
            } catch (Exception e) {
                SALog.printStackTrace(e);
            }
        }
        if (bout != null) {
            try {
                bout.close();
            } catch (Exception e) {
                SALog.printStackTrace(e);
            }
        }
        if (connection != null) {
            try {
                connection.disconnect();
            } catch (Exception e) {
                SALog.printStackTrace(e);
            }
        }
    }
}

