import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.util.function.Consumer;
import java.util.List;
import java.io.UncheckedIOException;

public class OsmInputFile {
    private final Path path;
    private final boolean lazy;

    public OsmInputFile(Path path, boolean lazy) {
        this.path = path;
        this.lazy = lazy;
    }

    private int readInt(FileChannel channel) throws IOException {
        ByteBuffer buffer = ByteBuffer.allocate(4);
        channel.read(buffer);
        buffer.flip();
        return buffer.getInt();
    }

    private byte[] readBytes(FileChannel channel, int length) throws IOException {
        ByteBuffer buffer = ByteBuffer.allocate(length);
        channel.read(buffer);
        buffer.flip();
        byte[] bytes = new byte[length];
        buffer.get(bytes);
        return bytes;
    }

    private byte[] readBytes(FileChannel channel, long offset, int length) throws IOException {
        channel.position(offset);
        return readBytes(channel, length);
    }

    private BlobHeader readBlobHeader(FileChannel channel) throws IOException {
        int headerLength = readInt(channel);
        byte[] headerBytes = readBytes(channel, headerLength);
        return new BlobHeader(headerBytes);
    }

    private FileChannel openChannel() throws IOException {
        return FileChannel.open(path, StandardOpenOption.READ);
    }

    public OsmBlockSource lazyReader() {
        return new LazyReader();
    }

    class LazyReader implements OsmBlockSource {
        private FileChannel channel;

        LazyReader() throws IOException {
            this.channel = openChannel();
        }

        @Override
        public void forEachBlock(Consumer<Block> consumer) {
            try {
                long pos = 0;
                while (pos < channel.size()) {
                    channel.position(pos);
                    BlobHeader header = readBlobHeader(channel);
                    int dataLength = header.getDataLength();
                    long dataOffset = pos + 8;
                    LazyBlock block = new LazyBlock(dataOffset, dataLength, channel);
                    consumer.accept(block);
                    pos = dataOffset + dataLength;
                }
            } catch (IOException e) {
                throw new UncheckedIOException(e);
            }
        }

        @Override
        public void close() throws IOException {
            if (channel != null) {
                channel.close();
            }
        }
    }

    record LazyBlock(long offset, int length, FileChannel channel) implements Block {
        @Override
        public Iterable<OsmElement> decodeElements() {
            try {
                byte[] data = readBytes(channel, offset, length);
                return parseOsmElements(data);
            } catch (IOException e) {
                throw new UncheckedIOException(e);
            }
        }

        private Iterable<OsmElement> parseOsmElements(byte[] data) {
            return List.of();
        }
    }
}

